INSERT INTO book_nodes VALUES (1,'دليل الإجراءات','branch','الفرع الأول : مبادئ العلاقات القانونية','',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (2,'دليل الإجراءات','sub_title','أولا : التعريف بالعلاقات القانونية',' أ- مفهوم القاعدة القانونية وعناصرها ۱ - تعريف القانون القانون لغة : هو العلاقة الثابتة بين شيئين ، وتعني النظام والتنظيم. القانون اصطلاحا: هي مجموعة القواعد العامة والمجردة التي تنظم سلوك الأفراد في المجتمع العلاقات الاجتماعية ) ، وهي ملزمة بتوقيع الدولة الجزاء القانوني على من يخالفها. ٢- موضوع القانون تنظيم العلاقات الاجتماعية / العلاقات القانونية ) : والعلاقات القانونية: هي الروابط والعلاقات الاجتماعية بين الأفراد والتي نظمها القانون بقواعد عامة ومجردة وفرض لها حماية قانونية بقواعده الملزمة ، ووضع لها جزاءً عند مخالفتها. - فائدة حماية القانون للعلاقات ١- حماية الأفراد واحترام حقوقهم العامة والخاصة (الشخصية والمالية) وكذا السياسية. ٢ - حماية المجتمع ، وذلك بإقامة النظام والاستقرار فيه وحفظ الأمن والسكينة مما يؤدي إلى تطوره في كافة الجوانب الحضارية . - إقامة التوازن بين حقوق الأفراد وحقوق المجتمع بحيث لا يغلب جانب على آخر. ٤ - عناصر القاعدة القانونية تقوم القاعدة القانونية على عنصرين هما عنصر الفرض وعنصر الحكم والجزاء القانوني. ۱ - عنصر الفرض وهي الواقعة المفترض وجودها لتطبيق الحكم والجزاء القانوني 44 الموجود في القاعدة القانونية، ومصدر هذا العنصر هو الواقع المادي الملموس الذي يعيشه الفرد. وهي أيضا ، مجموعة المشاكل أو الحالات الواقعية التي توصف بأنها علاج فعّال للقاعدة القانونية. ٢ - عنصر الحكم والجزاء : وهو الأثر القانوني المترتب على تحقق عنصر الفرض والذي يكون عبارة عن جزاء قانوني يتم تطبيقه في حالة تحقق الفرض . وهو أيضا ، يُعد الحلول أو العلاجات الجذرية الذي تنبثق عن القاعدة القانونية إزاء مشكلة ما . مثال : قاعدة وجوب التعويض على من عمل عملا غير مشروع أضر بالغير. فالفرض هو ارتكاب الفعل غير المشروع مع شرط وجود ضرر حصل للغير بينما الحكم أو الجزاء القانوني هو لزوم التعويض لمن ارتكب الفرض السابق. ب خصائص القاعدة القانونية: ۱ - قواعد عامة ومجردة وعناصر العمومية تتمثل في كونها عامة من حيث الأشخاص والمكان والزمان. فمن حيث الأشخاص الوطنيين فتطبق عليهم شروط تطبيق القاعدة القانونية. ومن حيث المكان تطبق على كافة إقليم الوطن البري والبحري والجوي وما في حكمه ( مقر البعثات الدبلوماسية في الخارج السفن والطائرات الوطنية في المياه الدولية ). ومن حيث الزمان تطبق من يوم نفاذها على كافة الوقائع المستقبلية دون أن يكون لها أثر رجعي - قواعد اجتماعية : أي أن موضوع القواعد القانونية تنظم العلاقات الاجتماعية وسلوك الفرد في المجتمع، ولا يكون إلا في جماعة، بالتالي لا ينظم القانون علاقة السلوك الداخلي للأشخاص من نوايا وأفكار وخواطر ما لم يتم ترجمة ذلك إلى سلوك خارجي كالقصد الجنائي في القتل العمد. 45 - قواعد ملزمة : بمعنى فرض لها القانون حماية وذلك بتوقيع الجزاء المادي من قبل الدولة عند مخالفتها، والمتمثلة في الصور الآتية : ١- الجزاء الجنائي : ( عقوبات بدنية / إعدام - قطع - جلد - حبس أو عقوبات مالية / ....... غرامة ، مصادرة ٢ - الجزاء المدني : (البطلان التنفيذ العيني التعويض الفسخ). - الجزاء الإداري: لوم) تنبيه خصم من الراتب تأخير الترقية الفصل من الوظيفة). ٤ - الجزاء المهني : (الوقف المؤقت من ممارسة المهنة الوقف النهائي من ممارسة المهنة / سحب الرخصة - الغرامة ) . ج- مصادر تنظيم العلاقات القانونية: أ - مصادر أصليه (التقنين ) (1) التقنين الأساسي (الدستور): وهو أعلى تشريع في الدولة يبين فيها المبادئ الأساسية في الدولة، ويصدر من الشعب عن طريق الاستفتاء الشعبي، وقد يكون قابلا للتعديل حسب الضوابط المذكورة في الدستور، ويُشترط في الدستور ألا يخالف الشريعة الإسلامية. (٢) التشريع الرئيسي (القوانين العادية : وهي التشريعات التي تصدرها السلطة التشريعية (مجلس النواب بعد مناقشتها والتصويت عليها، بناء على اقتراح أحد أعضائها أو السلطة التنفيذية ( مجلس الوزراء ) وبعد التصويت عليه يُعرض على رئاسة الدولة للتصديق عليها، ويُشترط في القوانين ألا تخالف الدستور، وأحكام الشريعة الإسلامية. (۳) التشريع الفرعي (اللوائح): وهي التشريعات التي تصدرها السلطة التنفيذية (رئيس الجمهورية ، مجلس الوزراء ، أجهزة السلطة المحلية ) ، ويكون بناء على تفويض تشريعي من السلطة التشريعية. ويكون الغرض منها إما تنفيذ القوانين ( لوائح تنفيذية ) ، أو تنظيم مرفق عام لوائح 46 تنظيمية ) ، أو بغرض ضبط الأمن والسكينة في المجتمع ( لوائح الضبط ) ، ويُشترط فيها ألا تخالف القوانين ، وأحكام الشريعة الإسلامية . ب - مصادر احتياطية ١- مبادئ الشريعة الإسلامية ويتم الرجوع لأحكام الشريعة الإسلامية عند عدم وجود قاعدة قانونية مكتوبة في التقنين المأخوذ من الشريعة الاسلامية. والمقصود بأحكام الشريعة الإسلامية الأحكام التي جاءت بها الشريعة الإسلامية، ويتم الرجوع إليها إما من مصادرها المدونة (الكتاب السنة الإجماع)، وإما من مصادرها وقواعدها الاجتهادية (القياس الاستحسان المصلحة المرسلة ) ، ويُستأنس برأي من سبق من مجتهدين وفقهاء الشريعة الإسلامية بمذاهبها المختلفة بشرط ألا تكون شاذة ومخالفة للإجماع. ٢ - العرف وهي العادات والتقاليد الثابتة في المجتمع والتي لها احترام وقبول من كافة الأفراد، ويُشترط فيها ألا تخالف الدستور ومبادئ الشريعة الإسلامية والنظام العام والآداب العامة . - مبادئ العدالة : وهي المبادئ القائمة على العدل والموازنة بين حقوق الأفراد بشرط ألا تخالف مبادئ الشريعة الإسلامية. ج - مصادر تفسيرية ۱ - مصادر التفسير التشريعي : وهي التي تصدرها السلطة التشريعية من مذكرات إيضاحية وكتب شارحة وأعمال تحضيرية وكذا المصادر التاريخية والمادية للتشريعات. ٢- مصادر التفسير الفقهي : وهي التفسيرات القانونية التي يوردها فقهاء القانون في شروحهم للتشريعات سواء في مؤلفاتهم أو محاضراتهم وندواتهم ومؤتمراتهم وتعليقهم على الأحكام القضائية ويدخل فيها رأي الفقه الإسلامي بمذاهبه المختلفة. ٣ - مصادر التفسير القضائي : وهي التفسيرات التي يوردها القضاء بمحاكمة المختلفة عند نظر النزاعات المتعلقة بالعلاقات القانونية ، وأهمها المبادئ القضائية التي جرى عليها أحكام المحكمة العليا. 47',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (3,'دليل الإجراءات','sub_title','ثانيا : عناصر العلاقات القانونية',' أ- سبب العلاقات القانونية (العمل القانوني) ١ - عمل قانوني إرادي (تصرف قانوني ) وهي العمل القانوني القائم على إرادة إيجاد علاقة قانونية، والتي أساسها التراضي الخالي من أي عيب (إكراه، غلط، تدليس، غبن ). وهي إما بإدارة منفردة (إيجاب فقط ) كالوقف والنذر والوعد بجائزة للجمهور ونحوها. وإما أن تكون بإرادة متعددة / عقد إيجاب وقبول ) ، مثل : عقد الزوج والبيع والإيجار والرهن ونحوها. ٢ - عمل قانوني لا إرادي ( واقعة قانونية ) وهي التي ليس للإرادة دور في إيجادها . وهي إما واقعة طبيعة كالولادة والموت والالتصاق ونحوها . وإما أن تكون فعلا مجردا : بصورتيه الفعل الضار ( المسئولية التقصيرية ) وهو الخطأ الذي يولد ضررا كالإتلاف ونحوه . والفعل النافع : كالإنفاق على اللقطة والإثراء بلا سبب . ب أطراف العلاقات القانونية (الأشخاص) / أصحاب المراكز القانونية: ۱ - شخص طبيعي (الإنسان) وتبدأ شخصيته من ولادته حيًا إلى وفاته حقيقة أو حكما (المفقود). ويكتسب بها المراكز القانونية والحقوق المترتبة عليها (أهلية وجوب ) . وتثبت له ميزات الشخصية القانونية من تعيبين (اسم، موطن ) . وحالة (سياسية/ الجنسية دينية / الديانة عائلية / القرابة نوعيه / الجنس، اجتماعية / عازب ، متزوج ، أرمل ) . كما تثبت له أهلية إبرام التصرفات القانونية ( أهليه أداء / تصرف ) ، وتثبت له أهلية 48 أداء كاملة من بلوغه ( ١٥ سنة ) عاقلا راشداً، وتكون تصرفاته صحيحة ، وقد تكون له أهلية أداء ناقصة وتكون تصرفاته موقوفة على إذن الولي من بلوغه سن التمييز (١٠ سنوات)، أما قبل سن التمييز فتكون أهلية الأداء منعدمة ( عديم أهليه ) ، وتكون تصرفاته باطلة. ٢ - الشخص المعنوي : وتثبت لمجموعة من الأشخاص والأموال، ويمثلها شخص طبيعي (ممثل الشخص المعنوي). وتتميز الشخصية الاعتبارية : بذمة مالية مستقلة وأهليه تصرف وأهليه تقاضي. وتنقسم إلى أشخاص معنوية عامة تابعة للدولة (الوزارات – المحافظات المؤسسات الحكومية - الأوقاف - النقابات المهنية). وأشخاص معنوية خاصة ( كالجمعيات المؤسسات الخاصة - الشركات). ج- محل العلاقة القانونية (الحقوق القانونية) ١ - الحقوق غير المالية : وهي الحقوق التي يكون محلها غير قابلة للتصرف ولا تنتقل للورثة ، ومن صورها : حقوق عامة : تثبت للشخص بصفته عضوا في المجتمع (الحرية، والمساواة). حقوق سياسية : وهي التي تثبت للشخص باعتباره عضوًا في المجتمع السياسي مثل : ( حق الانتخاب - حق الترشيح - حق تولي الوظائف العامة ) . حقوق الأسرة : وهي التي تثبت للشخص باعتباره عضوًا في الأسرة كحقوق الزوجين والأقارب. - ٢) الحقوق المالية : وهي الحقوق التي يكون محلها قابلة للتصرف وتنتقل للورثة، ويكون محلها مالا، وتنقسم بحسب نوع المال إلى : حقوق عينية : وتكون إذا كان المال محل الحق مالا منقولا معينا بالذات ( قيمية ) أو عقارا كانت كحق الملكية. 49 - حقوق شخصية حقوق في الذمة ديون) وتكون إذا كان المال محل الحق مالا منقولا معينا بالنوع ( مثلية ) أو نقودا أو أعمالا ، كالحق في الثمن والأجرة. ٣) الحقوق المشتركة (المعنوية ) وهي حقوق لها جانبان مالي وغير مالي ، كحقوق المؤلف ، كما يلي : - الجانب المالي : حيث يجوز التصرف فيها ويورث ، كالحق في استغلال المصنف. - الجانب غير المالي : كالحق في نسبة المصنف إلى مؤلفه وحق نشر المؤلف أو سحبه فلا يجوز التصرف فيه ولا يورث كالحقوق غير المالية.',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (4,'دليل الإجراءات','sub_title','ثالثا : صور العلاقات القانونية',' أ- العلاقات القانونية العامة علاقات الدولة القانون العام) ١ - العلاقة بين سلطات الدولة وتنشأ بنشوء الدولة والمتمثلة في اكتمال وجود عناصرها (إقليم - شعب – سلطة اعتراف ) وهي السلطات الثلاث التشريعية مجلس النواب، والسلطة التنفيذية (رئاسة الجمهورية - مجلس الوزراء ، أجهزة السلطة المحلية ) ، والسلطة القضائية. والعلاقة بينهم إما علاقة تعاون بقيام السلطة التشريعية بسن القوانين التي تنفذها السلطة التنفيذية ، وتطبقها السلطة القضائية في حالة المنازعات حولها. وهناك علاقة رقابة ممثله في رقابة السلطة التشريعية على التنفيذية باستجواب أعضائها وحل الحكومة ، وكذا رقابة السلطة القضائية على السلطة التشريعية في حالة إصدارها قوانين مخالفة للدستور ( رقابة دستورية ) ، وكذا رقابة السلطة القضائية على السلطة التنفيذية في حالة مخالفتها للقوانين ( رقابة إدارية ) وينظم العلاقات السابقة القانون الدستوري. ٢ - العلاقة بين الأفراد والدولة وتتمثل في حقوق الأفراد وواجباتهم ومن أهم حقوق الأفراد حقه في المشاركة في الحكم، إما بنفسه ( حق الترشيح ) أو بإنابة من يقوم مقامه ( حق الانتخاب)، وينظمها 50 القانون الدستوري، وكذا حقه في المشاركة في الإدارة عن طريق الوظيفة العامة (حق تولى الوظائف العامة وينظمها القانون الإداري، وكذا الحريات العامة التي كفلها الدستور (الحياة - الرأي - الدفاع ...). أما واجبات الأفراد فأهمها أداء الالتزامات المالية أمام لدولة (الزكاة، الضرائب، الرسوم ) وينظمها القانون المالي، والحفاظ على المال العام والنظام العام والآداب العامة والدين والوطن والوحدة. وينتج عن الإخلال بها حق للدولة في إيقاع الجزاء بالعقوبات المقررة قانونا في قانون العقوبات. ب العلاقات القانونية الخاصة (علاقات الأفراد) ۱ - علاقات قانونية شخصية ( قانون أحوال شخصية ) وهي إما علاقة إرادية كالعلاقة الناشئة بسبب عقد الزواج (علاقة الزواج ) والذي ينتج عنها حقوق الزوجين ( مهر، نفقة ، طاعة)، وإما أن تكون علاقة لا إرادية كعلاقة القرابة بسبب وجود قرابة النسب وما ينتج عنها من حقوق القرابة (نفقة ، ولاية ، إرث ). ٢ - علاقة قانونية مالية ( قانون مدني - تجاري - عمل ) وهي التي يكون محلها ما لا والمال إما أن يكون عينا معينة بالذات (عقار أو منقول ) فتكون العلاقة هنا علاقة ملكية لذلك العين ( حق الملكية ) ، وإما أن يكون المال عينا معينة بالنوع أو دينا في الذمة (نقود - منقولات مثلية ، عمل امتناع عن عمل ) صارت تلك العلاقة مديونية أطرافها دائن ومدين . والعلاقات المالية إما أن تكون مدنية بين أشخاص مدنيين كانت علاقة مدينة وينظمها القانون المدني، أو علاقات مالية تجارية بين التجار وينظمها القانون التجاري، وإما أن تكون علاقات عمل بين العمال وأصحاب العمل وينظمها قانون العمل. ج- العلاقات القانونية الدولية ۱ - علاقات قانونية دولية عامة (قانون دولي عام ) وهي العلاقات القانونية بين الدول، وهي إما أن تكون علاقة تعاون دولي بين كافة 51 دول المجتمع الدولي بسبب اشتراكها في عضوية المنظمات الدولية (الأمم المتحدة، جامعة الدول العربية ، منظمة التجارة الدولية ) أو بسبب اشتراكها في توقيع المعاهدات الدولية ( الإعلان العالمي لحقوق الإنسان - حقوق الطفل ...... الخ ) . وقد تكون العلاقات القانونية بين الدول ثنائية ، وهي إما في حالة السلم كالعلاقات الدبلوماسية والاتفاقيات الثنائية. وإما أن تكون العلاقة في حالة الحرب كالالتزام بعدم استخدام الأسلحة المحرمة دوليا وحماية المدنيين ومراعاة حقوق الأسرى، وهذه العلاقة ينتج عنها علاقة الحياد للدول التي خارج الحرب كحق ضبط المهربات الحربية والحصار البحري. - علاقات دولية خاصة (قانون دولي خاص ) وهي علاقات خاصة بين الأفراد تخللها عنصر أجنبي، وهذا العنصر الأجنبي قد يوجد في أحد عناصر العلاقات القانونية الخاصة ( سبب - أطراف - محل ) ، فتكون العلاقة هنا تنازع قوانين . أو قد يكون العنصر الأجنبي في أحد عناصر علاقة الخصومة بين الأفراد، فيعرف بتنازع الاختصاص القضائي ، إذا اختلف الدعوى أو محلها أو سببها. د العلاقات القانونية الإجرائية (الخصومة) ١ - الخصومة الدستورية والإدارية وهي التي يكون موضوعها الإخلال بحق من الحقوق العامة (دستورية - إدارية)، والقضاء المختص هو القضاء الدستوري الدائرة الدستورية في المحكمة العليا) أو القضاء الإداري (المحاكم الإدارية ) وتوقع الجزاء الدستوري والإداري (الإلغاء التأديب التعويض ) . ٢ - الخصومة المدنية : وهي الخصومة التي موضوعها الإخلال بالحقوق الخاص ( شخصية - مدنية - تجارية ) ، وسببها دعوى يرفعها صاحب الحق المعتدى عليه (المدعي ) ضد المعتدي (المدعى عليه)، والمحكمة المختصة هي القضاء المدني بفروعه الشخصي والمدني والتجاري، ويوقع على المعتدي الجزاء المدني البطلان التنفيذ العيني - التعويض - الفسخ). 52 ٣- الخصومة الجنائية: وهي التي يكون موضوعها ارتكاب المخالفات (الجرائم ) الواردة في قانون العقوبات، وسبب هذه الخصومة قرار اتهام ترفعه النيابة العامة بناء على بلاغ أو شكوى ويكون المجني عليه والمدعى بالحق الشخص أو المدني طرفا منضما إلى النيابة العامة، والمحكمة المختصة هي القضاء الجنائي (القسم الجنائي في المحاكم العامة - أو المحاكم الجزائية المتخصصة)، وتحكم المحكمة بالجزاء الجنائي (الإعدام - القطع الجلد - الحبس - الغرامة - المصادرة - التدابير الاحترازية ) . ٤- خصومة التنفيذ: وهي الخصومة التي تقع بسبب عدم التنفيذ الاختياري للسندات التنفيذية (أحكام قضائية - أحكام تحكيم - أوامر على عرائض ) ، وسببها طلب تنفيذ يتقدم به طالب التنفيذ ضد المنفذ ضده إلى المحكمة المختصة (المحكمة الابتدائية - محكمة الاستئناف في حالة تنفيذ أحكام التحكيم). ه - خصومة دولية وهي خصومة موضوعها الإخلال بمبادئ القانون الدولي وحقوق الدول في حالة السلم والحرب. وقد تكون خصومة دولية مدنية كمنازعات الحدود والاستغلال البحري، والقضاء المختص هي محكمة العدل الدولية أو هيئات التحكيم الدولية. وإما أن تكون خصومه دوليه جنائية ، وهي المتعلقة بالإخلال بقواعد القانون الدولي حالة الحرب (جرائم الحرب)، والمحكمة المختصة هي محكمة الجنايات الدولية في لاهاي. 53',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (5,'دليل الإجراءات','branch','الفرع الثاني : مفهوم العلاقة القانونية العقدية','',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (6,'دليل الإجراءات','sub_title','أولا : مفهوم صياغة العقود وأهميتها :',' تعني مهارة صياغة العقود : قدرة الشخص في تحويل إرادة الأطراف إلى قالب مكتوب ، الغرض منه ضبط المراكز القانونية الناتجة عنه ، ومنع حصول أي نزاع حول هذه العقود والتصرفات من حيث تفسير إرادة الأطراف ونحوها . والقائم بصياغة العقود : بصفة أساسية هم الأمناء والموثقون والمحامون والأعضاء القانونيون والموظفون في الإدارات القانونية. ويجب على هؤلاء مراعاة ضوابط الصياغة القانونية موضوعا بمعرفة موضوع العقد والتصرف، وشكلا وفنيا . وصياغة العقود لا تقف عند النواحي الشكلية من ضبط المصطلحات وتدقيق العبارات وملاحظة الجوانب اللغوية وتصنيف أحكامها وترتيبها، بما يكفل للعقد الدقة وحسن الصياغة وكمال التنسيق بل الصياغة تعني مراجعة بنود مشروع العقد، وتشمل الإجراءات التي تسبقه وجميع ما يعتبر جزءًا منه، للوقوف على مدى مطابقتها لأحكام القانون ومن ثم تنقيح العقد من المخالفات التي يمكن أن تشوب إبرامه أو بنوده ، تحسبًا للوقوع في خطأ قانوني قد يؤثر على تنفيذ العقد أو على مدى صحته من الوجهة القانونية. وتظهر أهمية صياغة العقود في كونها تقوم بتنظيم المراكز القانونية الخاصة والحقوق والالتزامات، وتنظيم كيفية إنشائها، وتحديد الالتزامات الناشئة عنها، وكذا كيفية تنفيذها والجزاء القانوني لها.',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (7,'دليل الإجراءات','sub_title','ثانيا تعريف العقد',' تعريف العقد لغة : نقيض الحل، ويكون بمعنى الشد والربط والإحكام والوثاق. تعريف العقد اصطلاحا : توافق إرادتين أو أكثر على إحداث أثر قانوني معين سواء كان هذا الأثر هو إنشاء التزام أو نقله أو تعديله أو انهاؤه. والعقد في القانون اليمني : إيجاب من أحد المتعاقدين يتعلق به قبول من الآخر وما يدل عليهما على وجه يترتب أثره في المعقود عليه (المحل). 54 الفرق بين العقد والاتفاقية : الاتفاق هو تلاقي إرادة الأطراف على إحداث أثر قانوني أما العقد فهو أخص من الاتفاق ينشئ التزاما فقط تكييف العقد: تصرف قانوني بإرادتين يحتاج إلى إيجاب وقبول بينما التصرف الانفرادي / الإرادة المنفردة تحتاج إلى إيجاب فقط دون قبول.',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (8,'دليل الإجراءات','sub_title','ثالثا: أنواع العقود',' أ- العقد المدني: العقد المدني : هو العقد المالي الذي ينظمه القانون المدني بحيث يكون الغرض منه إشباع حاجات شخصية وليس الربح والمضاربة. عقود ترد على الملكية : مثل : عقد البيع، عقد السلم، عقد الصرف، القرض، شركة العقود ، عقد الصلح ، عقود التبرع ( الهبة ، الوصية ، الهدية ) . عقود ترد على المنفعة : مثل : عقد الإيجار (الاجارة ) ، عقد المضاربة (القراض)، المقاولة والالتزام بالمرافق العامة ، الوكالة ، الوديعة ، العارية. عقود الضمان ( الوثيقة ) عقد الرهن ، الكفالة (الضمانة). عقود متفرقة : عقد المعاش أو المرتب مدى الحياة، عقد التأمين . ب - العقد التجاري: العقد التجاري : هو العقد الذي يبرمه التاجر بمناسبة عمل تجاري . العمل التجاري : هو العمل الذي يقوم بها الشخص بقصد تحقيق الربح ولو كان غير تاجر. صور العقود التجارية : عقد البيع التجاري - بيع النائب لنفسه بيع المباني السكنية والتجارية - البيع بالتقسيط البيع بطريق التصفية والمزاد - نقل الأشخاص - نقل البضائع الإيداع في المخازن العامة الممثلون التجاريون الوكالة بالعمولة الحساب الجاري بين الأشخاص - رهن تجاري الكفالة التجارية - بيع متجر - رهن- متجر - عقود تأسيس الشركات - شراء أسهم الشركات - عقود الصرف السمسرة الوكالة بالعمولة - الإيداع في المخازن العامة التأمين التجاري - عقود النقل البري - عقود توزيع الماء والكهرباء 55 والغاز - عقود وكالات الأعمال - عقد وكالة العقود - عقود التوريد - عقود الطبع والنشر - عقود الوكالات الإعلانية - عقود مقاولات بناء العقارات وترميمها وتعديلها وهدمها - عقود التصنيع. عمليات البنوك : الإيداع (وديعة النقود - الحساب الجاري - إجارة الخزائن ). عميلات البنوك الائتمانية : السحب على المكشوف الاعتماد المستندي - خطاب الضمان - سلف البنوك . عقود التجارة البحرية: إنشاء سفينة - بيع سفينة - رهن سفينة - إيجار واستئجار السفينة إصلاح السفينة تأمين بحري النقل البحري للبضائع النقل البحري للأشخاص - عقد العمل البحري البيوع البحرية (فوب، فاس، سيف، سي أنداف) - عقود شراء مستلزمات السفينة والملاحة البحرية - القرض البحري. عقود التجارة الجوية إنشاء الطائرة - بيع الطائرة رهن طائرة - إيجار واستئجار الطائرة - إصلاح الطائرة - تأمين جوي - النقل الجوي للبضائع النقل الجوي - عقد نقل الأشخاص - عقد العمل الجوي. ج- العقد الإداري: - العقود الإدارية : هو تلاقي إرادتين (إيجاب وقبول ) بين شخصين أحدهما الدولة (أشخاص القانون العام ) - تتعلق بإدارة مرفق عام - وتكون للدولة فيها امتيازات خاصة، مثل: عقد امتياز المرافق العامة إدارة مرفق اقتصادي كمؤسسة الكهرباء ) - عقد الأشغال العامة ( المقاولة ) - عقد التوريد لمرفق عام . ويتم إبرام العقد الإداري : إما بالمناقصة - أو الممارسة - أو بالأمر المباشر. ويحق للإدارة في العقد الإداري : الإشراف والتوجيه - توقيع الجزاءات على المتعاقد تعديل شروط العقد إلغاء العقد - وبالمقابل عليها التزام دفع المقابل المالي والتعويض. مميزات العقد الإداري: أن يكون أحد طرفي العقد شخصا اعتباريا عاما. أن يتصل العقد بمرفق عام. أن يتضمن العقد شروطا استثنائية غير مألوفة في القانون الخاص. 56 0-0-0- صور وأنواع العقود الإدارية : - عقد التزام المرافق العامة : هو عقد إداري بمقتضاه تعهد الدولة إلى فرد أو شركة خاصة بإدارة واستغلال مرفق اقتصادي عام لمدة محددة من الزمن، وتحت إشراف ورقابة الإدارة، ويقوم الملتزم (فرد أو شركة ) بإدارة المرفق على مسئوليته وعلى نفقته وبعماله، مقابل مزايا مادية وعينية، أهمها الرسوم التي يتقاضاها الملتزم من المنتفعين من المرفق العام. - عقد الأشغال العامة: عقد بين الإدارة وبين أحد الأفراد وبين أحد الأفراد أو إحدى الشركات ويُسمى مقاول، بمقتضاه يتعهد المقاول بالقيام ببناء أو ترميم أو صيانة مباني أو أكثر منشآت على عقار لحساب شخص اعتباري عام ؛ وتحقيقا لنفع عام، وذلك مقابل ثمن معين في العقد. - عقد التوريد : هو عبارة عن تعاقد بين الإدارة وبين مورد خاص ( فرد أو شركة ) بتوريد أشياء منقولة تكون لازمة لمرفق عام ، وذلك مقابل ثمن مبين في العقد. د - العقد الدولي: - العقد الدولي : هو العقد الذي تخلل أحد عناصره عنصر أجنبي أو في مكان تنفيذه. - مزايا العقد الدولي : لا يتقيد بقانون وطني، لجوئه إلى التحكيم والطرق الودية لتسوية المنازعات ، اعتماده على نماذج موحدة ، اعتماده على اتفاقيات ومعاهدات دولية. - صور العقد الدولي : عقود امتياز البترول ، عقود الأشغال الدولية ، عقود نقل التكنلوجيا. - قواعد الإسناد لمعرفة القانون الواجب التطبيق في العقد الدولي الخاص : - قانون إرادة الأطراف الصريحة أو الضمنية. - قانون الموطن المشترك. - قانون محل إبرام العقد في حال اختلاف الموطن. 57',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (9,'دليل الإجراءات','branch','الفرع الثالث : أركان وجود العلاقة القانونية العقدية','',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (10,'دليل الإجراءات','sub_title','أولا : التراضي (الصيغة)',' أ- تعريف التراضي: هو توافق إرادتين على إحداث أثر قانوني معين، فهو يفترض وجود إرادتين متطابقتين. ب طرق التعبير عن التراضي: لفظ : يدل على مدلول العقد بدلالة جازمة . كتابة : مفهومة ومستبينة ، عرفية أو رسمية ، كتابة أو طباعة. إشارة : بشرط أن تكون مفهمة للطرف الآخر وللشهود، وتكون في حالة العجز عن النطق أو الكتابة. الفعل : كالمعاطاة ونحوها . السكوت الملابس ويكون في حال القبول فقط مع وجود ظروف وقرائن تدل عليه. ج- عناصر التراضي: الإيجاب: هو التعبير البات والجازم عن إرادة شخص يعرض على الآخر التعاقد معه - هو الإرادة الأولى، ويسقط في حال رفض الطرف الآخر، كما يحقق للموجب الرجوع قبل صدور القبول من الطرف الآخر. القبول : هو التعبير البات الصادر عمن وجه إليه الإيجاب، ويجب صدوره قبل رجوع الموجب ، كما يجب مطابقته للإيجاب. اقتران الإيجاب والقبول ( مجلس العقد ) وهو يعني اجتماع المتعاقدين في نفس الزمان والمكان بحيث يسمع كل منهما الآخر مع انشغالهما بالتعاقد وعدم انصرافهما عنه، ويتحقق انفضاض المجلس بالتفرق المادي بالأبدان ، أو المعنوي بالانشغال عن العقد، ويثبت للمتعاقدين بعد انعقاد العقد بالإيجاب والقبول في مجلس العقد بالتراجع عن العقد من كليهما ما داما في مجلس واحد لم يتفرقا ويُعرف هذا بخيار المجلس. 58 وفي حالة التعاقد بين غائبين : ينعقد العقد عند علم الموجب بالقبول، وينتج عن هذا عدم صحة رجوع الموجب عن إيجابه بعد ذلك. د- عيوب العقد: - الإكراه : هو حمل القادر على غيره على ما لا يرضاه قولا أو فعلا. - الغلط: وهم يقوم في ذهن المتعاقد فيصور له أمرا على غير حقيقته فيدفعه إلى التعاقد. - التدليس / الغرر: هو استعمال وسائل احتيالية بقصد دفع الشخص للتعاقد. - الغبن : هو عدم التعادل في القيمة وقت إبرام العقد بين المتعاوضات، ولا يكون عيبا في التراضي إلا في الغبن الفاحش (عشر القيمة). وعيوب العقد السابقة تجعل العقد قابلا للإبطال (البطلان النسبي ).',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (11,'دليل الإجراءات','sub_title','ثانيا : المتعاقدان ( طرفا العقد)',' أ- الأهلية: - أهلية التصرف / الأداء / التعاقد : وهي صلاحية الشخص لممارسة العمل القانوني وإبرام التصرفات القانونية. وأساس هذه الأهلية هي العقل والرشد ، ويكتسبها من بلغ السن القانونية ( ١٥ سنة في المعاملات المدنية و ١٨ سنة في المعاملات التجارية ) بشرط ثبوت العقل والرشد. وتزول هذه الأهلية بالجنون والعته، وتكون تصرفاته باطلة مطلقة كالصبي غير المميز. كما تُعتبر ناقصة في حال السفه، الذي تكون تصرفاته موقوفة على إذن الولي، كالصبي المميز (سنه ١٠ سنوات ). أما بالنسبة لذي العاهة الجسمية (المعوق بدنيا ) فيعين له القاضي مساعد قضائي لمساعدته في التصرف والتعاقد ولا يستطيع هذا المساعد الانفراد بالتعاقد دون إذن ذي العاهة. 59 0-0-0- ب الولاية: الولاية الأصلية : وهي ملكية الشخص لمحل التعاقد والتصرف، وبالتالي لا يمكن لغير المالك التصرف في الشيء وإلا كان فضوليا، ويكون تصرفه معلقا على إذن المالك وإجازته. الولاية بالنيابة (النيابة في التعاقد ) : ويجوز للشخص التعاقد في ملك غيره بناء على نيابة قانونية صحيحة. وهي إما إجبارية تثبت للأب أو وصيه أو الجد أو وصيه أو لمنصوب القاضي، وتكون هذه النيابة خاصة بالقاصر عديم الأهلية أو ناقصها. أو نيابة اتفاقية واختيارية بموافقة الأصيل عن طريق الوكالة . ويكون دور النائب إجباريا أو اختياريا ) هنا إبرام التصرف، وتنصرف إليه آثار العقد من مراكز قانونية إلى الأصيل دون النائب فقط.',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (12,'دليل الإجراءات','sub_title','ثالثا : محل العقد (المعقود عليه )',' أ- صور محل العقد: عين ثابتة / عقار - عين منقولة / سلعة - منقول معنوي / الحقوق الفكرية - نقد منفعة / خدمات. ب شروط محل العقد: مشروع : بأن يوافق حكم الشرع موجود : بأن يكون موجودا حال التعاقد. معلوم : للمتعاقدين علما نافيا للجهالة. ممكن : بأن يكون ممكنا ومقدورا على تسليمه والقيام به . 60 ربعًا : بطلان العقد أ- البطلان المطلق للعقد: وهو الجزاء المقرر لتخلف أركان العقد من تراضي ومحل وشروط وجودها السابقة، ومن صورها أيضا التعاقد مع عديم الأهلية المجنون والصبي غير المميز. ويكون العقد في هذه الحالة منعدما وغير موجود ويحق لأي طرف أو للغير التمسك به ، كما يجب على القاضي الحكم به من تلقاء نفسه . وهذا البطلان لا يتصحح بالإجازة ولا بمضي المدة، ومرور فترة التقادم الطويل تجعل المتعاقد الحائز لمحل العقد الباطل يملكه بالحيازة وليس بالعقد. ب - البطلان النسبي وهو الجزاء المقرر للعقد الذي شابه عيب من عيوب التراضي أو التعاقد مع ناقص الأهلية الصبي المميز أو السفيه. وهذا العقد يكون قابلا للإبطال أو التصحيح ( الإجازة ) من الشخص الذي وقع العيب في حقه فقط. ويسقط الحق في إبطال العقد بالإجازة الصريحة أو الضمنية والمتمثلة في التصرف في محل العقد. ويدخل في حكم الإجازة الضمنية سكوت صاحب الحق في الإبطال حتى مضت المدة القانونية المعتبرة ( ثلاث سنوات ) ، والتي يتم احتسابها من تاريخ البلوغ أو العلم بالعيب. 61',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (13,'دليل الإجراءات','branch','الفرع الرابع : آثار العلاقة القانونية العقدية','',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (14,'دليل الإجراءات','sub_title','أولا : الجانب الموضوعي لتنفيذ العقد',' نقل الملك : كنقل ملكية المبيع للمشتري . نقل المنفعة : كنقل منفعة العين المؤجرة للمستأجر. ترتيب الدين في الذمة : كترتيب دين الثمن في ذمة المشتري . الالتزام بعمل معين : كالتزام المقاول بالبناء. التوثيق للديون : كترتيب حق الرهن على العين المرهونة لصالح الدين المرهون به .',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (15,'دليل الإجراءات','sub_title','ثانيا : الجانب الشخصي لتنفيذ العقد',' يسري العقد بين أطرافه وبين خلفهما العام ( الورثة ) والخاص (المشتري لعين معينة). لا يسري العقد على الدائنين إلا في حالة المحافظة على الضمان العام - لا يسري العقد على الغير إلا إذا رضي الغير.',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (16,'دليل الإجراءات','sub_title','ثالثا: الجزاء العقدي (المسئولية العقدية)',' إذا حصل إخلال من المدين المتعاقد بالتزاماته نتج عن ذلك حصول المسئولية العقدية في حق ذلك المتعاقد، فالمسئولية العقدية هي جزاء عدم تنفيذ العقد، وينتج عنها تعويض الدائن من قبل المدين عن ذلك . ويتحقق حصول المسئولية العقدية بتوافر أركانها من خطأ وضرر وعلاقة سببية. ويُعرف ركن الخطأ في المسئولية العقدية بالخطأ العقدي، ويتمثل في عدم تنفيذ الالتزام العقدي، إما كليا أو جزئيا أو تنفيذا معيبا أو تنفيذا متأخرا. ويختلف الخطأ العقدي بحسب نوع الالتزام فإذا كان الالتزام هو تحقيق نتيجة كالتزام البائع بتسليم المبيع، فيثبت الخطأ بعدم حصول النتيجة وهي عدم تسليم المبيع ، أما إذا كان الالتزام هو بذل عناية كالتزام المحامي في عقد المحاماة، 62 فيثبت الخطأ هنا بعدم بذل القدر الواجب من العناية للشخص العادي. أما ركن الضرر في المسئولية العقدية، فيتمثل في ضرورة وجود ضرر أصاب المتعاقد بسبب خطأ المتعاقد الآخر. ويثبت الضرر بصورتيه : الضرر المادي الذي يصيب الشخص في ماله، وكذا الضرر الأدبي الذي يصيب الشخص في كيانه الأدبي لسبب يمس عاطفته أو شعوره أو كرامته ، ويُشترط في الضرر أن يكون محققا ومتوقعا . أما ركن علاقة السببية : فتعني أن الضرر الذي أصاب المضرور ناتج عن خطأ المسئول، وتنقطع علاقة السببية بحصول الضرر بسبب قوة قاهرة أو بسبب أجنبي أو بسبب المضرور ذاته. وينتج عن تحقق أركان المسئولية العقدية التزام المسئول بتعويض المضرور. كما يحق للمتعاقدين تعديل قواعد المسئولية العقدية بالتشديد فيها كأن يتحمل الملتزم الآخر تبعة القوة القاهرة. وبالمقابل يجوز للمتعاقدين الاتفاق على الإعفاء من المسؤولية العقدية، ما لم يكن هناك غش من المسئول أو وقوع خطأ جسيم، ويجوز الاتفاق على الإعفاء من الخطأ الجسيم أو الغش إذا حصل ذلك من الغير. يجوز لأطراف العقد على وضع جزاءات خاصة للعقد .، كما يلي : ١- عدم تنفيذ العقد وحبس ما لدى المتعاقد الآخر من حقوق عينية ونقدية. ٢ - الاتفاق على تحديد التعويض في حالة عدم التنفيذ، فيما يُعرف بالشرط الجزائي، ويكون ذلك الشرط تحت رقابة القاضي من حيث تقدير وجوده أو تعديله لاسيما في حالة عقود الإذعان. - الاتفاق على وضع جزاء الفسخ في حال عدم تنفيذ العقد، ويكون ذلك أيضا متروك تقديره لسلطة القاضي.',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (17,'دليل الإجراءات','sub_title','رابعا : انتهاء العلاقة القانونية العقدية',' بسبب الوفاء بالالتزامات ( انقضاء العقد ( الوفاء - انتهاء المدة - انتهاء الغرض. بسبب إرادة الأطراف : التفاسخ . 63',NULL,NULL,NULL);
