INSERT INTO book_nodes VALUES (19,'دليل الإجراءات','sub_title','أولا : الجانب الموضوعي لتنفيذ العقد',' نقل الملك : كنقل ملكية المبيع للمشتري . نقل المنفعة : كنقل منفعة العين المؤجرة للمستأجر. ترتيب الدين في الذمة : كترتيب دين الثمن في ذمة المشتري . الالتزام بعمل معين : كالتزام المقاول بالبناء. التوثيق للديون : كترتيب حق الرهن على العين المرهونة لصالح الدين المرهون به .',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (20,'دليل الإجراءات','sub_title','ثانيا : الجانب الشخصي لتنفيذ العقد',' يسري العقد بين أطرافه وبين خلفهما العام ( الورثة ) والخاص (المشتري لعين معينة). لا يسري العقد على الدائنين إلا في حالة المحافظة على الضمان العام - لا يسري العقد على الغير إلا إذا رضي الغير.',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (21,'دليل الإجراءات','sub_title','ثالثا: الجزاء العقدي (المسئولية العقدية)',' إذا حصل إخلال من المدين المتعاقد بالتزاماته نتج عن ذلك حصول المسئولية العقدية في حق ذلك المتعاقد، فالمسئولية العقدية هي جزاء عدم تنفيذ العقد، وينتج عنها تعويض الدائن من قبل المدين عن ذلك . ويتحقق حصول المسئولية العقدية بتوافر أركانها من خطأ وضرر وعلاقة سببية. ويُعرف ركن الخطأ في المسئولية العقدية بالخطأ العقدي، ويتمثل في عدم تنفيذ الالتزام العقدي، إما كليا أو جزئيا أو تنفيذا معيبا أو تنفيذا متأخرا. ويختلف الخطأ العقدي بحسب نوع الالتزام فإذا كان الالتزام هو تحقيق نتيجة كالتزام البائع بتسليم المبيع، فيثبت الخطأ بعدم حصول النتيجة وهي عدم تسليم المبيع ، أما إذا كان الالتزام هو بذل عناية كالتزام المحامي في عقد المحاماة، 62 بسبب الاتفاق بين الأطراف على فسخ العقد اتفاقا في حالة عدم تنفيذه. بسبب خلل في الأركان : بطلان مطلق - بطلان نسبي. بسبب خلل في تنفيذ العقد : فسخ قانوني. بسبب زوال محل العقد : انفساخ العقد.',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (22,'دليل الإجراءات','branch','الفرع الخامس : الجوانب الفنية في العقد','',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (23,'دليل الإجراءات','sub_title','أولا : مراحل صياغة العقد:',' أ- مرحلة التحضير: - الاطلاع على المستندات. - جمع بيانات أطراف التعاقد. قراءة المراسلات والمكاتبات ومحاضر المفاوضات. ب مرحلة التفكير - دراسة المستندات. مناقشة المراكز الناشئة عن العقد. - تكييف الوقائع المنشئة للعقد. - تسمية العقد وتكييفه . - تحديد قانون العقد للرجوع إليه. ج- مرحلة الإصدار: صياغة وكتابة العقد. مراجعة وتنقيح مسودة العقد مع الأطراف. قراءة العقد على الأطراف والشهود. التوقيع على العقد من أطرافه ومن الشهود ومن أي شخص له صفة. التوثيق والتسجيل لدى الجهات المختصة. 64',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (24,'دليل الإجراءات','sub_title','ثانيا : البيانات المطلوبة لصياغة العقد',' اسم العقد تاريخ العقد مكان العقد أطراف العقد وصفاتهم محل العقد بنود العقد مدة العقد القانون الواجب التطبيق حل منازعات العقد (المحكمة المختصة ، شرط التحكيم) الجزاء العقدي أسماء الشهود وبياناتهم نسخ العقد التوقيعات والأختام. وثائق العقد هويات الأشخاص الوكالات حكم انحصار الوراثة حكم التنصيب وثيقة التمثيل القانوني ناظر الوقف سنديك التفليسة (مصفي التفليسة ) الحارس القضائي وثائق الملكية ( بصيرة / بيع - نقال - هبة - فصل قسمة ) . 65',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (25,'دليل الإجراءات','sub_title','ثالثا : هيكل العقد / مكونات العقد:',' البسملة اسم العقد زمان العقد ومكانه الديباجة والتمهيد : فكرة عامة عن العقد الباعث على العقد الاتفاقات السابقة ظروف العقد - الإشارة إلى طبيعة كل عقد واحتياج الأطراف - مدى علاقة العقد بشيء سابق مثل : حدث - عقد أو تصرف - قرار إداري - مفاوضات - حكم قضائي قرار محكمة - حكم قضائي - إجراءات المناقصة . بنود العقد : التأكيد على دخول المقدمة في العقود - وصف محل العقد ثمن العقد، التزامات الطرف الأول الموجب / المملك التزامات الطرف الثاني -مدة العقد السرية والحفاظ على الملكية الفكرية - القانون الواجب التطبيق - تسوية المنازعات (دقة شرط التحكيم) - انتهاء العقد وفسخه الإخطارات والإعلانات والمراسلات - انتهاء العقد وترتيبات ما بعد العقد. مشتملات الالتزامات : ماهية الالتزام - المدين به - كيفية الوفاء به زمانا ومكانا ونفقة الجزاء العقدي (صور الإخلال / عدم تنفيذ كلي - تنفيذ جزئي - تنفيذ معيب - تنفيذ متأخر - جزاء الإخلال / بطلان - تنفيذ عيني - تنفيذ بمقابل - تعويض نقدي - فسخ ) . حوالة العقد والتنازل عنه وانتقال الالتزامات الناشئة عنه. الضمانات والتأمينات. تعديل العقد - تمديده . التعريفات والمصطلحات. التراخيص وموافقات الجهات الرسمية. تفسير العقد. القوة القاهرة. أثر عدم التنفيذ. 66 - متفرقات : التدريب - نقل التقنية السرية الحقوق الفكرية - خدمة ما بعد البيع - الدعم الفني والتقني التركيب والتشغيل التسويق وسائله ومنافذه - ملاحق العقد : التوقيع عليها - ذكرها في العقد.',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (26,'دليل الإجراءات','sub_title','رابعا : قواعد شكلية وفنية في صياغة العقود',' وهذه القواعد هي ما يلي : - تجنب استخدام الكلمات الغامضة. - تجنب استخدام الكلمات المثيرة للالتباس. - استخدام الكلمة نفسها بالمفهوم ذاته، واستخدام كلمة مختلفة للتعبير عن مفهوم مختلف. - لا تستخدم الكلمات غير الضرورية. - استخدام الكلمات الواردة في القوانين ذات الصلة. - تجنب العبارات الوصفية والظرفية المثيرة للالتباس - استخدام «واو» العطف وحرف «أو» بعناية. - للتوضيح ، استخدام التبنيد ( بنود العقد) - استخدام صيغة الإثبات ولا تستخدم صيغة النفي. - تجنب استخدام صيغة «يكون» في كل أشكالها. - استخدام مفردات كلمات تلائم مستخدمي القانون. - ضع المفهوم الأكثر أهمية في نهاية الجملة، وحدد موقع العبارات التابعة والوصفية. - قواعد الإحالة بطريقة صحيحة . - استخدام الجمل القصيرة. - استخدام صيغة المبني للمعلوم. - استخدام صيغة الإثبات. - استخدام الجملة الفعلية (الفعل المضارع). 67 مراعاة الربط بين الجواب والشرط عند استخدام الجملة الفعلية. مراعاة الربط بين الفرض والحكم العقدي. استعمال الإحالة بحذر. استعمال المصطلحات الدارجة والمألوفة. الثبات في استعمال المصطلح . استعمال الضمائر بشكل صحيح. 68',NULL,NULL,NULL);
