INSERT INTO book_nodes VALUES (95,'دليل الإجراءات','sub_title','رابعا : الحصص :',' أ) اتفق الشركاء على توزيع الحصص كالتالي : ١ - حصة الشريك الأول تساوي ( ) من رأس المال. 220 ٢ - حصة الشريك الثاني تساوي ( ) من رأس المال. - حصة الشريك الثالث تساوي ( ) من رأس المال . ب ) الحصص قابلة للانتقال بين الشركاء وكذلك ورثتهم الشرعيين ، ولا يجوز لأي شريك التنازل عن حصة أو أكثر من حصصه للغير بعوض أو بغير عوض إلا بموافقة باقي الشركاء كتابيا.',47,NULL,NULL);
INSERT INTO book_nodes VALUES (96,'دليل الإجراءات','sub_title','خامسا : مدة العقد :',' مدة هذا العقد ................. تبدأ من تاريخ توقيعه من قبل الأطراف وتنتهي في ................ ما لم يخطر أحد الطراف برغبته في إنهاء العقد قبل ............... شهر على الأقل بخطاب مسجل على عنوانه .',47,NULL,NULL);
INSERT INTO book_nodes VALUES (97,'دليل الإجراءات','sub_title','سادسا : الإدارة :',' يتولى إدارة الشركة الشركاء مجتمعون أو منفردون (اختيار مدير ويحدد له الاصلاحية والمحظورات )',47,NULL,NULL);
INSERT INTO book_nodes VALUES (98,'دليل الإجراءات','sub_title','سابعا : الأرباح :',' ١- يتم تحديد الأرباح الصافية للشركة طبقًا للميزانية السنوية التي تعد حسب الأصول المحاسبية من واقع الدفاتر والمستندات والفواتير ....... وتحدد الأرباح الصافية بعد خصم المصروفات. ٢ - توزع الأرباح الصافية بين الشركاء وفقًا لنسب حصصهم في رأس المال... (تحدد الفترة وموعد توزيع الارباح ). ثامنا : تتخذ قرارات الشركة بالإجماع . تاسعا : لا يجوز لأي شريك دون موافقة الشركاء الآخرين أن يمارس لحسابه أو لحساب الغير نشاطا من نوع نشاط الشركة ، وإذا أخل أحد الشركاء بهذا الالتزام كان للشركة أن تطالبه بالتعويض أو أن تعتبر العمليات التي قام بها لحسابه الخاص قد تمت لحساب الشركة. 221 عاشرا : اتفق الشركاء على فتح حساب شخصي مشترك بينهم لدى .................................................... يعتمد فيه توقيع الشركاء أو من يفوضونه . حادي عشر: حرر هذا العقد من ( .......... ) نسخ بيد كل شريك نسخة .',47,NULL,NULL);
INSERT INTO book_nodes VALUES (99,'دليل الإجراءات','sub_title','ثاني عشر: أقر الشركاء بأنهم قد اطلعوا على بنود هذا العقد بعناية تامة تنفي الجهالة وقد',' وقعوا عليه برضاهم واختيارهم. تم هذا بحضور الشهود الأخ / - بطاقة عائلية جواز سفر) صادرة من ................... برقم ( .......... ) وتاريخ / / م، والأخ / يحمل .................................... (بطاقة شخصية - بطاقة عائلية - جواز سفر) صادرة من ........... برقم ( .......... ) وتاريخ / / م، وتم قراءة هذا المحرر على الأطراف والشهود المذكورين، ثم تمت المصادقة والإبهام عليه من قبلهم أمامي أنا محرر هذا المحرر .................................... ( الموثق أو الأمين أو المختص ) / ............. (الاسم كاملا)، وتوقيعاتهم وإبهامهم أدناه حجة عليهم والله خير الشاهدين. ..................................... يحمل .................................... ( بطاقة شخصية إبهام الاطراف إبهام إبهام شاهد شاهد توقيع وختم محرر الوثيقة / العقد ٢ - عقد التأسيس لشركة .... المحدودة إنه في يوم ......................... وتاريخ : / / ١٤٤ه الموافق : / / ٢٠٢م وبمدينة ......................... تم الاتفاق بين الموقعين أدناه على ما يلي : مادة (1) تأسيس الشركة: أن يؤسسوا شركة يمنية ذات مسئولية محدودة بناء على ترخيص من مراقب الشركات طبقًا لأحكام القانون رقم (٢٢) لسنة 1997م بشأن الشركات التجارية وتعديلاته والنظام الأساسي المرفق. مادة (٢) : اسم الشركة : شركة ................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................ المحدودة مادة (۳) اغراض الشركة -۱ -٢ -۳ 222 0-0-0- مادة (٤) : مركز الشركة يكون مركز الشركة ومحلها القانوني في مدينة ويجوز بقرار من الجمعية العامة غير العادية للشركاء أن تنشئ لها فروع أو مكاتب أو توكيلات في الجمهورية اليمنية أو في الخارج. مادة (٥) : مدة الشركة مدة الشركة هي سنة / سنوات تبدأ من تاريخ صدور الترخيص بتأسيس الشركة ويجوز اطالة هذه المدة بقرار من الجمعية العامة غير العادية، خاضع للتصديق عليه من وزارة الصناعة والتجارة. مادة (٦) : رأس مال الشركة : حدد رأس مال الشركة بمبلغ وقدره ( ( يمني يحدد كتابة ورقما ) موزع على ( ) حصة قيمة كل حصة ) ريال يمني. مادة ( ۷ ) : أ اكتتب الشركاء في جميع حصص الشركة وفقًا للبيان التالي : م ۱ د ۳ اسم الشريك ريال عدد الحصص القيمة النسبة ( الإجمالي العام ب - دفعت قيمة الحصص النقدية بكاملها في بنك %۱۰۰ وهو من البنوك المعتمدة من قبل الدولة ولا يجوز للبنك تسليم المبالغ المودعة لدية لحساب الشركة تحت التأسيس إلا لإدارة الشركة بعد إتمام التأسيس أو إعادتها للمؤسسين إذا صرف النظر عن تأسيس الشركة بموجب مذكرة رسمية من الوزارة. مادة (۸) : وافق المؤسسون على النظام الأساسي للشركة المرفق بهذا العقد وتعهدوا بالسعي لاستيفاء إجراءات التأسيس والحصول على الترخيص بإنشاء الشركة وإجراء معاملات إشهارها في السجل التجاري طبقًا للقانون النافذ. 223 مادة (٩): يتحمل المؤسسون جميع نفقات التأسيس كلا بحسب حصته في رأس مال الشركة في حالة عدم اكتمال إجراءات التأسيس أما في حالة اكتمال تأسيس الشركة فأنها تصبح جزء من النفقات العامة للشركة. مادة (١٠) : حرر هذا العقد من عدد من النسخ لكل شريك نسخة وثلاثة نسخ تودع أحدهما لدى مراقب الشركات ونسخة لدى إدارة الشركات في الوزارة ونسخة لإيداعها في السجل التجاري بعد إتمام إجراءات التأسيس والإشهار للشركة. والله ولي التوفيق ،،، شخصية / م اسم الشريك الجنسية جواز التاريخ / مكان الاصدار موبايل + ايميل التوقيع ۱ د ۳ ٣- النظام الأساسي لشركة ......................................................................... المحدودة',47,NULL,NULL);
INSERT INTO book_nodes VALUES (100,'دليل الإجراءات','chapter','الفصل الأول :',' تأسيس الشركة مادة (1) تأسيس الشركة : تأسست طبقًا لأحكام القانون رقم (٢٢) لسنة ١٩٩٧م بشأن الشركات التجارية وتعديلاته ولهذا النظام شركة يمنية ذات مسئولية محدودة بحسب الأحكام المبينة فيما تلي هذه المادة. مادة (٢): اسم الشركة : شركة ...................................................................................................................................................................................................................................................................................................................................................................................................................... المحدودة مادة (۳) اغراض الشركة ۱ - - 224 0-0-0- مادة (٤) : مركز الشركة ويجوز يكون مركز الشركة ومحلها القانوني في مدينة بقرار من الجمعية العامة غير العادية للشركاء أن تنشئ لها فروع أو مكاتب أو توكيلات في الجمهورية اليمنية أو في الخارج. مادة (٥) : مدة الشركة مدة الشركة هي عامًا تبدأ من تاريخ صدور الترخيص بتأسيس الشركة ويجوز اطالة هذه المدة بقرار من الجمعية العامة غير العادية ، خاضع للتصديق عليه من وزارة الصناعة والتجارة.',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (101,'دليل الإجراءات','chapter','الفصل الثاني :',' (رأس مال الشركة وملكية الحصص ) مادة (٦): رأس مال الشركة حدد رأس مال الشركة بمبلغ وقدره ..............) ريال يمني (يحدد كتابة ورقما ) موزع على .............) حصة قيمة كل .................... حصة ريال يمني مادة (۷) أ - اكتتب الشركاء في جميع حصص الشركة وفقًا للبيان التالي : م اسم الشريك ۱ د ۳ الإجمالي العام عدد الحصص القيمة النسبة %۱۰۰ ب . دفعت قيمة الحصص النقدية بكاملها في بنك وهو من البنوك المعتمدة من قبل الدولة ولا يجوز للبنك تسليم المبالغ المودعة لدية لحساب الشركة تحت التأسيس إلا لإدارة الشركة بعد إتمام التأسيس أو إعادتها للمؤسسين إذا صرف النظر عن تأسيس الشركة بموجب مذكرة رسمية من الوزارة. 225 0-0-0- مادة (۸): زيادة رأس المال : أ- يجوز زيادة رأس المال عن طريق رفع قيمة الحصة او زيادة عدد الحصص بقرار من الجمعية العامة غير العادية يوافق عليه شريك او أكثر يمثل ٧٥٪ من راس المال على الاقل خاضعًا لتصديق وزارة الصناعة والتجارة. ب - في حال زيادة راس المال يكون للشركاء حق الافضلية بنسبة ما يملك كل منهم من حصص . مادة (٩): تخفيض رأس المال : أ- يجوز تخفيض رأس المال اذا زاد على حاجة الشركة او اذا منيت بخسائر بموجب قرار من الجمعية العامة غير العادية خاضعًا لمصادقة وزارة الصناعة ووفقًا للشروط التالية : ١- ألا يقل راس المال عن الحد الأدنى المقرر وفقًا للاحكام النظامية. ٢- بعد سماع تقرير مراقب الحسابات القانوني يبين فيه أسباب التخفيض والالتزامات التي على الشركة وأثر التخفيض في هذه الالتزامات. - الإعلان بتخفيض رأس المال في أحد الصحف اليومية الرسمية وكذلك إخطار الدائنين بهذا القرار برسائل مسجلة إلى مواطنهم المعلومة . - على الدائنين أن يقدموا إلى الشركة المستندات المثبتة لديونهم خلال ستين يومًا من تاريخ الإعلان أو الإخطار لتقوم الشركة بوفاء الديون الحالية منها وتقديم الضمانات الكافية للوفاء بالديون الآجلة. لا تتم المصادقة على تخفيض رأس المال ولا يسري قرار التخفيض إلا بعد استيفاء الشروط السابقة. ب - بعد استيفاء الإجراءات السابقة يمكن أن يتم التخفيض أما بإنقاص قيمة الحصص أو بإلغاء بعض الحصص بإنقاص عددها أو بشراء بعضها بما يوازي الجزء المراد تخفيضه من الاحتياطي. 226',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (102,'دليل الإجراءات','chapter','الفصل الثالث :',' انتقال الحصص مادة (١٠) : أ لكل شريك الحق أن يتنازل للغير عن حصته في الشركة أو عن جزء منها لا تقل قيمته عن خمسة آلاف ريال على ألا يترتب على هذا التنازل الإخلال بالأحكام القانونية. ب - إذا كان المتنازل له غير شريك في الشركة فللشركاء الآخرين حق الرجحان عليه واسترداد الحصص المبيعة بنفس الشروط. ج - لممارسة حق الرجحان المذكور في الفقرة السابقة على من يعتزم من الشركاء بيع حصته او جزء منها إلى شخص غير شريك أن يخطر إدارة الشركة وسائر الشركاء بذلك مبينا اسم ولقب المتنازل له وجنسيته ومهنته وعنوان إقامته وعدد الحصص المزمع التنازل عنها وقيمتها المعروضة والمتفق عليها وشروط العرض الأخرى، وعلى إدارة الشركة أن تقوم بدورها خلال خمسة أيام على الأكثر من تاريخ تسلم الإخطار بتبليغ الإخطار المذكور بكتاب مسجل إلى جميع الشركاء ولهؤلاء أن يبينوا رغبتهم في شراء الحصص المعروضة بنفس الشروط خلال مدة شهر واحد من تاريخ تبليغ الإخطار وللشركاء خلال هذه المدة تحديد رغبتهم لاستعمالهم حق الرجحان واسترداد الحصص. د. بعد انقضاء شهر على إبلاغ العرض دون أن يستعمل أحد الشركاء حق الرجحان يكون الشريك الراغب بالبيع حرًا في التصرف بحصته. ه إذا استعمل حق الرجحان أكثر من شريك قسمت بينهم الحصص المبيعة بنسبة حصة كل منهم في الشركة. مادة (١١) : أ - يجوز تقسيم الحصص على ألا يزيد عدد الشركاء على ثلاثين. ب - إذا توفي الشريك تنتقل ملكية حصصه إلى ورثته الشرعيين. ج - إذا انتقلت حصة بالإرث إلى أكثر من شخص وكان عددهم يؤدي إلى زيادة عدد الشركاء على ثلاثين تبقى حصص جميع الورثة بحكم حصة واحدة بالنسبة للشركة ما لم تنتقل الحصة باتفاقهم أو بحكم القضاء إلى عددهم منهم ضمن الحد الأقصى لعدد الشركاء. 227 مادة (١٢) : أ- تمسك ادارة الشركة سجل خاص تدون فيه أسماء الشركاء وألقابهم وجنسياتهم ومهنهم وعنوان كل منهم وعدد الحصص التي يمتلكها كل شريك . ب - يدون في السجل المذكور وقائع التنازل عن الحصص واسم المتنازل له وعدد الحصص المتنازل عنها وقيمتها وتاريخ التنازل وتوقيع رئيس مجلس الإدارة وتوقيع المتنازل والمتنازل له مالم توفر وثيقة تنازل أخرى تثبت توقيعهما، كما تدون فيه الحصص المنتقلة بالإرث وأسماء من انتقلت منهم واليهم هذه الحصص ورقم وتاريخ حصر الإرث. ج - للشركاء ودائني الشركة حق الاطلاع على هذا السجل عند الاقتضاء. د. يجب تبليغ وزارة الصناعة والتجارة إدارة الشركات والسجل التجاري عن إي كل تغيير يطرأ على الشركة أو الشركاء واخذ إشعار بذلك. ه في جميع الأحوال لا يترتب على انتقال الحصص او التنازل عنها أثر بالنسبة إلى الشركاء او إلى الغير إلا من تاريخ القيد في السجل المذكور ولدى إدارة الشركات والإشهار في السجل التجاري. و- يصبح من آلت إليه الحصة بالتنازل أو الانتقال شريكا في الشركة وله حقوق سائر الشركاء طبقًا لأحكام قانون الشركات وهذا النظام. مادة (۱۳): يجوز للشركة أن تصدر (شهادة مطابقة لقيود السجل بالحصص التي يمتلكها كل شريك. مادة (١٤) : لا يلتزم الشريك إلا بحدود حصته في الشركة ولا يجوز زيادة التزاماته . مادة (١٥) : يترتب حتمًا على ملكية الحصة قبول نظام الشركة وقرارات جمعيتها العامة. مادة (١٦) آخر مالك للحصة في سجل الشركة يكون له الحق في قبض المبالغ المستحقة سواء من الأرباح أو من موجودات الشركة. مادة (۱۷): لا يجوز للشركة إصدار اسناد قرض. 228',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (103,'دليل الإجراءات','chapter','الفصل الرابع :',' إدارة الشركة مادة (١٨) : أ- يتولى إدارة الشركة مدير عام يتم تعيينه من قبل الجمعية العامة للشركة. ب - باستثناء ما يحظره هذا النظام أو الجمعية العامة يتولى المدير العام الصلاحيات التالية : ١- تمثيل الشركة في علاقتها مع الغير وأمام القضاء. ٢- إجراء العقود والمعاملات الداخلة ضمن اغراض الشركة وتصريف شئون الشركة اليومية بما لا يخالف قرارات الجمعية العامة وهذا النظام. ٣- التوقيع عن الشركة وتلتزم الشركة بتوقيع مدير عام الشركة ضمن اغراض الشركة شريطة انتفاء سوء النية أو الغش أو التواطوء وبما يوافق أحكام القانون واللوائح النافذة. ج - تقيد سلطة المدير العام في الأمور التالية : ١- مزاولة أي غرض منافس لأغراض الشركة أو أي أغراض أخرى مكملة الأغراض الشركة لحسابه ٢- بيع أورهن أصول وممتلكات الشركة إلا بموافقة الجمعية العامة للشركاء. مادة (١٩): يكون عزل المدير العام بقرار من الجمعية العامة للشركة. مادة (٢٠) للمدير العام حق اعتزال الإدارة بشرط اختيار الوقت الملائم وأخطار الشركة مقدمًا قبل مدة لا تقل عن ثلاثة أشهر. مادة (٢١): جميع العقود والمراسلات والاتصالات والإعلانات وبصورة عامة وجميع الوثائق والمطبوعات والمعلومات والنشرات التي تصدر عن الشركة يجب أن تحمل اسم الشركة وان يتبع ذلك كلمة (محدودة) مع بيان رأس المال بأحرف وأرقام واضحة وإذا وقع المدير العام باسمه الخاص دون إبراز صفته كممثل عن الشركة فلا تلتزم الشركة إلا بحدود ما أثرت به مادة (٢٢): يتحدد راتب المدير العام باجر مقطوع أو بنسبة من الأرباح أو بكلا الأسلوبين معا بموجب قرار من الجمعية العامة للشركاء. 229 مادة (٢٣) : أ يجب على المدير العام وعلى مدراء الفروع والمكاتب والوكالات التابعة للشركة مسك دفاتر حسابات الشركة بالطرق المحاسبية الصحيحة وبموجب أحكام القوانين والأنظمة النافذة. ب - لا يجوز لمدير عام الشركة بغير موافقة الجمعية العامة أن يتولى الإدارة في شركة أخرى منافسة أو ذات أغراض مماثلة أو أن يقوم لحسابه أو لحساب الغير بعمليات في تجارة مماثلة أو منافسة للشركة على الإطلاق. مادة (٢٤) : على كل شريك القيام قدر الامكان والسعي بتسهيل اعمال الشركة وإنجاحها وحماية مصالحها والامتناع عن ممارسة أي نشاط يلحق الضرر بها تحت طائلة المسئولية والتعويض. مادة (٢٥) : أ- على المدير العام تقديم تقرير سنوي عن نشاط الشركة ووضعها المالي مرفق بالميزانية والحسابات الختامية للشركة مدعمًا بالتقرير الكامل لمراقب الحسابات عن مطابقة حساباتها لوضعها المالي والوثائق طبقًا للأنظمة والقوانين النافذة وذلك في موعد إقصاء ثلاثون يوما من تاريخ انتهاء السنة المالية ليتسنى عرضه على الجمعية العامة في الموعد المحدد. ب - يجب أن تحفظ دفاتر الشركة في مركزها الرئيسي أو محل أعمالها ولكل شريك أو وكيله الرسمي حق الاطلاع على أي دفتر للشركة ونسخ أي قيد فيه كلما أراد.',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (104,'دليل الإجراءات','chapter','الفصل الخامس :',' الجمعية العامة',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (105,'دليل الإجراءات','sub_title','أولا : أحكام عامة',' مادة (٢٦) : أ - الجمعية العامة تمثل جميع الشركاء وهي أعلى سلطة في الشركة. ب - لكل شريك حق حضور اجتماع الجمعية العامة عن نفسه أو وكالة عن شريك أخر بالشركة أو أكثر. ج - يشترط لصحة التوكيل في الحضور الجمعية العامة ان يكون التوكيل ثابتًا بالكتابة وخاصا بكل اجتماع . د. لكل شريك من الأصوات بقدر عدد ما يملكه ويمثله من حصص دون تحديد. 230 مادة (٢٧) : أ على المدير العام دعوة الجمعية العامة للشركة للانعقاد في مركز الشركة مرة واحدة على الأقل في السنة أو كلما طلب ذلك عدد من الشركاء يمثلون ربع رأس مال الشركة . ب - توجه الدعوة بخطاب مسجل قبل الموعد المعين للاجتماع بثمانية أيام على الأقل وترسل لكل شريك في موطن إقامته ويجب أن تشمل الدعوة على جدول الأعمال والوثائق اللازمة. ج - توجه الدعوة ايضًا إلى وزارة الصناعة والتجارة لإيفاد مندوب عنها لحضور اجتماع الجمعية العامة لتكون قراراتها صحيحة . مادة (۲۸) : يرأس الجمعية العامة مدير عام الشركة او من تختاره الجمعية من بين أعضائها وللجمعية حق تعيين أمين سر ومراجع لفرز الأصوات. مادة (٢٩) : أ الجمعية العامة للشركاء هي السلطة العليا في الشركة وعلى مدير عام الشركة تنفيذ قراراتها . ب - على الشركة إخطار وزارة الصناعة والتجارة بقرارات الجمعية العامة وتسليم صور من محاضر الجلسات وصورة من الميزانية وتقرير مدير عام الشركة ومدققي الحسابات. مادة (٣٠) : لكل شريك أثناء انعقاد الجمعية حق مناقشة الموضوعات والمسائل المدرجة في جدول الأعمال ويكون مدير عام الشركة ملزما بالإجابة على أسئلة الشركاء بالقدر الذي لا يعرض مصالح الشركة للضرر وإذا رأى أحد الشركاء أن الرد غير كافي احتكم للجمعية العامة ويكون قراراها واجب التنفيذ في كل ما يحتكم فيه إليها. مادة (۳۱) : تدون وقائع مداولات الجمعية العامة وقراراتها في محضر يقيد في سجل خاص ويوقع عليه رئيس الجمعية وأمين السر ومندوب الوزارة. مادة (٣٢) لوزارة الصناعة والتجارة حق الإشراف والرقابة على الشركة ضمن الشروط المنصوص عليها في الأحكام الخاصة بالشركات المساهمة. 231 0-0-0-',47,NULL,NULL);
INSERT INTO book_nodes VALUES (106,'دليل الإجراءات','sub_title','ثانيا الجمعية العامة العادية',' مادة (۳۳) : أ - يجب انعقاد الجمعية العامة العادية خلال الأشهر الثلاثة الأولى من انتهاء السنة المالية وكلما اقتضت الحاجة للانعقاد. ب - تتولى الجمعية العامة العادية بصورة خاصة الاختصاصات التالية : ۱- تعيين المدير العام. ٢- عزل المدير العام وفق أحكام القانون وهذا النظام. - الموافقة على تقرير المدير العام عن نشاط الشركة وابراء ذمته أو تقرير رفع دعوى المسئولية عليه . - الموافقة على تقرير مراقب الحسابات والميزانية العامة للشركة وحساب الأرباح والخسائر. ه- تعيين مراقبي الحسابات للسنة والسنين القادمة وتحديد أجورهم وأتعابهم. ٦- المصادقة على اللوائح والأنظمة الداخلية المنظمة لأعمال الشركة. مادة (٣٤) : أ- يعتبر اجتماع الجمعية العامة العادية صحيحًا إذا حضره شركاء يمثلون الأكثرية المطلقة لراس المال. ب - تصدر قرارات الجمعية العامة العادية بموافقة عدد من الشركاء الحائزين بالأصالة أو الوكالة على أغلبية رأس المال.',47,NULL,NULL);
INSERT INTO book_nodes VALUES (107,'دليل الإجراءات','sub_title','ثالثا الجمعية العامة غير العادية',' مادة (٣٥) : أ - تنعقد الجمعية العامة غير العادية وتتخذ قراراتها بأغلبية شركاء يمثلون ( ٧٥%) من رأس مال الشركة على الأقل. ب تختص الجمعية العامة غير العادية بما يلي : ١ - تعديل النظام الأساسي للشركة. ٢ - زيادة أو تخفيض رأس مال الشركة. - تحويل شكل الشركة القانوني إلى شكل قانوني آخر. ٤ - دمج الشركة في شركة أخرى. ه - حل وتصفية الشركة. 232 ج - يجب على رئيس مجلس الادارة شهر التعديل لدى السجل التجاري بالوزارة.',47,NULL,NULL);
INSERT INTO book_nodes VALUES (108,'دليل الإجراءات','chapter','الفصل السادس :',' مالية الشركة مادة (٣٦) : تبدأ السنة المالية للشركة من أول يناير من كل عام وتنتهي في الواحد والثلاثين من شهر ديسمبر من نفس العام على ان السنة الأولى تشمل المدة التي تنقضي من تاريخ تأسيس الشركة حتى نهاية السنة المالية. مادة (۳۷) : أ- يتم تعيين مراقب حسابات أو أكثر بقرار من الجمعية العامة من الجدول التي تضعه الوزارة، ولمدة سنة تكون قابلة للتجديد بقرار من الجمعية العامة العادية للشركة. ب - يتولى مراقب الحسابات المهام التالية : ۱ تدوين الملاحظات المهمة التي يتوصل إليها أثناء عمله وبصفة خاصة التي لها أثر على المركز المالي للشركة وتدوّن الخطوات والإجراءات التي اتخذت بالنسبة للملاحظات التي ظهرت له أثناء عملية المراجعة. ٢- دراسة وفحص نظم الرقابة الداخلية والنظام المحاسبي الموضوع لقيد وإثبات العمليات في دفاتر وسجلات الشركة وتقييم مدى ملاءمة هذا • • • • النظام كأساس لما يلي : إعداد الميزانية وحساب الأرباح والخسائر. متابعة ومراقبة أوجه نشاط الشركة. المحافظة على أصول الشركة. تنفيذ القوانين واللوائح والتعليمات المتعلقة بنشاط الشركة. - الرفع بالتقارير الدورية عن حسابات ونشاط الشركة للوزارة طبقًا لنص المواد (٦٦- ٦٧ - ٦٨ - ٦٩ ) من اللائحة التنفيذية لقانون الشركات. مادة (۳۸) : أ على المدير العام أن يقدم عن كل سنة مالية ميزانية الشركة وحساب الإرباح والخسائر في موعد يسمح بعقد الجمعية العامة في خلال ثلاثة أشهر من تاريخ انتهاء السنة المالية . ب - على مدير عام الشركة أن يعد أيضا تقريرًا مفصلا عن نشاط الشركة خلال 233 السنة المالية وعن مركزها المالي في ختام السنة ذاتها. ج- يجوز لكل شريك خلال الأيام الخمسة عشر التي تسبق انعقاد الجمعية العامة أن يطلع بنفسه أو بواسطة وكيل يختاره من بين الشركاء أو من غيرهم على هذه الوثائق مادة (۳۹) : توزع أرباح الشركة السنوية بقرار من الجمعية العامة بناء على اقتراح مدير عام الشركة بعد خصم جميع التكاليف والمصروفات الموضحة على النحو التالي : أن تفرز من الأرباح المحققة الالتزامات المترتبة على الشركة بموجب قانون وأنظمة العمل. ب - تفرز الضرائب المستحقة على الشركة وتكوين المخصصات اللازمة دفع الالتزامات في مواعيدها وتحاشيا لاي نفقات متوقعة تخص السنة المالية. ج- يفرز بعد ذلك مبلغ بنسبة (۱۰) من الأرباح الصافية لتكوين احتياطي قانوني ، ويجوز للجمعية العامة وقف هذا الفرز متى ما بلغ مجموع الاحتياطي القانوني قدرًا يوازي نصف رأس المال على انه اذا مس هذا الاحتياطي تعين العودة للاقتطاع من الأرباح. د. يجوز للجمعية العامة إنشاء وإفراز احتياطي نظامي اختياري لمواجهة بعض الأمور العارضة محتملة الوقوع لمصلحة الشركة على الا تزيد النسبة عن (٪۱۰) من صافي الأرباح. ه تفرز المكافآت التي تقررها الجمعية العامة للإدارة ومراقبي الحسابات. وتفرز النسب المقررة لاستهلاك رأس المال على إلا تزيد عن (١٠). ز تفرز النسب المقررة والمتعارف عليها للاستهلاك موجودات الشركة (أصولها الثابتة ) بالطريقة التي تستهلك بها الشركة موجوداتها وبحسب ما هو متعارف عليه في الأنظمة المحاسبية. ح توزع الأرباح الباقية على أصحاب الحصص بنسبة حصصهم في المكان والمواعيد التي يحددها المدير العام والخسائر أن وجدت فيتحملها الشركاء بحسب النسب ضمن حدود قيمة حصة كل منهم ودون أن يكون متضامنا مع غيره من الشركاء . 234 مادة (٤٠) : يستعمل المال الاحتياطي بناء على قرار الجمعية العامة فيما هو أوفى بالمنافع لصالح الشركة .',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (109,'دليل الإجراءات','chapter','الفصل السابع :',' حل الشركة وتصفيتها مادة (٤١) : تنحل الشركة لأحد الأسباب التالية : أ بانتهاء المدة المحددة لها في عقد التأسيس والنظام الأساسي ولم تجدد. ب - باندماج الشركة في شركة أخرى. ج - بقرار من الجمعية العامة غير العادية. في حالة خسارة نصف رأس المال إلا إذا قررت الجمعية العامة غير العادية غير ذلك على ان لا يقل النصف الباقي من رأس المال عن الحد القانوني. بحكم قضائي بات من المحكمة المختصة لأسباب عادلة. د. ه مادة (٤٢): تعين الجمعية العامة طريقة التصفية وعدد المصفين وتحدد سلطاتهم. مادة (٤٣) : تنتهي سلطة المدير العام بمجرد تعيين المصفي أو المصفين وتبقى سلطة الجمعية العامة قائمة إلى أن يتم تصفية الشركة أو أخلاء عهدة المصفين.',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (110,'دليل الإجراءات','chapter','الفصل الثامن :',' أحكام ختامية مادة (٤٤) : أ - يسجل النظام الأساسي للشركة في وزارة الصناعة والتجارة ويشهر طبقًا للقانون. ب - تخصم كافة المصروفات والنفقات والتكاليف المدفوعة في سبيل التأسيس من حساب المصروفات العامة للشركة. ج - تكون تبليغات الشركة المشار إليها في عقد الشركة ونظامها الأساسي بكتاب مسجل مع العلم بالوصول سواء كانت بين الشركاء أو بينهم وبين الشركة. مادة (٤٥) : أ على جميع الشركاء والمديرين والمحاسبين القانونيين في الشركة التقييد 235 بأحكام هذا النظام وقرارات الجمعية العامة. ب - يرجع إلى قانون الشركات التجارية والقوانين والأنظمة النافذة في كل ما لم يرد عليه نص في هذا النظام. والله ولي التوفيق ،،، م اسم الشريك الجنسية شخصية / جواز التاريخ / مكان الاصدار موبايل + ايميل التوقيع ۱ د ٤ - عقد لتأسيس شركة تضامن: عقد التأسيس الشركة........................................................................ التضامنية',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (111,'دليل الإجراءات','chapter','الفصل الأول :',' تأسيس الشركة إنه في يوم ......................................................................... وتاريخ : / / ١٤٤ه الموافق : / / ٢٠٢م وبمدينة ........................................................................ تم الاتفاق بين الموقعون أدناه على ما يلي : مادة (1) : تأسيس الشركة: أن يؤسسوا شركة تضامنية طبقًا لأحكام القانون رقم (22) لسنة ١٩٩٧م بشأن الشركات التجارية وتعديلاته . مادة (٢): اسم الشركة: شركة ......................................................................... التضامنية. مادة (۳) : مركز الشركة: يكون مركز الشركة ومحلها القانوني في مدينة ......................................................................... ويجوز بقرار من الشركاء أن تنشئ لها فروع أو مكاتب أو توكيلات في الجمهورية اليمنية أو في الخارج. 236 مادة (٤) : أغراض الشركة: ۱- ٢- ٣- مادة (٥) : مدة الشركة: مدة الشركة هي ................................................... سنة / سنوات تبدأ من تاريخ التوقيع على هذا العقد وكل أطاله لمدة الشركة يجب أن تعتمد بقرار من الشركاء خاضع للتصديق عليه من وزارة الصناعة والتجارة.',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (112,'دليل الإجراءات','chapter','الفصل الثاني :',' رأس مال الشركة وملكية الحصص مادة (٦) : رأس مال الشركة: حدد رأس مال الشركة بمبلغ وقدره (................) (................)........... ريال يمني موزع على (...........) حصة قيمة كل حصة (................)............................................................................................................. ريال يمني. مادة (7) أ - اكتتب الشركاء في جميع حصص الشركة وفقًا للبيان التالي: م اسم الشريك عدد الحصص القيمة النسبة ۱ ٢ ٣ الإجمالي العام ٪۱۰۰ 237',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (113,'دليل الإجراءات','chapter','الفصل الثالث :',' إدارة الشركة مادة (۸) : يتولى إدارة الشركة / بصفته مديرًا عامًا للشركة . مادة (٩): يقوم المدير العام بتصريف أعمال الشركة اليومية وتسيير شئونها والتوقيع عنها ، كما يقوم بتمثيل الشركة أمام القضاء والجهات الرسمية وغير الرسمية وخلال تولية إدارة الشركة عليه أن يقوم بالعمل النافع بكل أمانة وإخلاص وان يبذل من العناية في تدبير مصالح الشركة ما يبذله في تدبير مصالحه الخاصة، وان يقدم عنها حسابات ومعلومات دقيقة إلى كل الشركاء وأن يمتنع عن أي نشاط يلحق الضرر بالشركة. مادة (١٠) : لا يجوز لأي شريك على انفراد أن يعقد باسم الشركة أي مشروعات لحسابه الخاص إلا بناء على موافقة خطية من شركاه الآخرين لمدة محددة وإذا لم تحدد المدة تعتبر المدة سنة واحدة وعلى كل شريك تقديم حسابات عن كل منفعة حازها بدون موافقة شريكه الأخر من آية معاملة تتعلق بها من جراء استعمال أموالها أو اسمها او علاماتها التجارية ، كما لا يجوز له أن يدير مشروعًا مشابه لمشروع الشركة إلا بمقتضى إجازة مكتوبة من شريكه أو شركاه الآخرين. مادة (١١) للشريك المتضامن أن يراقب مدير عام الشركة في أعماله وتقديم الآراء والنصائح والترخيص له في إجراء بعض التصرفات تجاوز حدود سلطته في سبيل مصلحة الشركة. مادة (١٢) في حالة تعرض الشركة لأي مشاكل يجب على كل شريك أن يبلغ شركاه الأخرين كتابيًا ليتم الوصول إلى حل موحد لتلك المشاكل. مادة (۱۳): يجوز لكل شريك أن يطلب من شريكه الأخر عقد جلسة أو جلسات لهم في أي وقت للنظر في أمور الشركة وبحث أوضاعها ووضع محاضر بذلك يوقع عليها الشركاء. 238',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (114,'دليل الإجراءات','chapter','الفصل الرابع :',' الحصص وانتقالها مادة (١٤) : إذا مات أحد الشركاء يجوز لورثة المتوفي عند وجودهم ان يطلبوا اعتبارهم شركاء موصين فان لم يطلبوا ذلك تحل الشركة. مادة (١٥) : يجوز لأي شريك من الشركاء الانسحاب من الشركة قبل انقضاء اجلها المحدد في العقد على شرط أن يبلغ شركاه الأخرين كتابيًا في فترة لا تقل عن شهرين.',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (115,'دليل الإجراءات','chapter','الفصل الخامس :',' مالية الشركة مادة (١٦): تبدأ السنة المالية للشركة في أول شهر يناير وتنتهي في الواحد والثلاثون من شهر ديسمبر من كل عام . مادة (۱۷): أ- يتم تعيين مراقب حسابات أو أكثر بقرار من الشركاء من الجدول التي تضعه الوزارة، ولمدة سنة تكون قابلة للتجديد بقرار من الشركاء في الشركة. ب - يتولى مراقب الحسابات المهام التالية : ۱ - تدوين الملاحظات المهمة التي يتوصل إليها أثناء عمله وبصفة خاصة التي لها أثر على المركز المالي للشركة وتدوّن الخطوات والإجراءات التي اتخذت بالنسبة للملاحظات التي ظهرت له أثناء عملية المراجعة . ٢- دراسة وفحص نظم الرقابة الداخلية والنظام المحاسبي الموضوع لقيد وإثبات العمليات في دفاتر وسجلات الشركة وتقييم مدى ملاءمة هذا النظام كأساس لما يلي: - إعداد الميزانية وحساب الأرباح والخسائر. - متابعة ومراقبة أوجه نشاط الشركة . - المحافظة على أصول الشركة. - تنفيذ القوانين واللوائح والتعليمات المتعلقة بنشاط الشركة. 239 - الرفع بالتقارير الدورية عن حسابات ونشاط الشركة للوزارة. مادة (١٨) : على الشركاء تقديم تقرير سنوي إلى الوزارة عن نشاط الشركة ووضعها المالي مرفقًا بالميزانية العامة والحسابات الختامية. مادة (۱۹): على الشركاء تقديم حسابات ومسك دفاتر حسابية وان تدون كل المعاملات اليومية والمالية ولا يجوز تأجيل قيد أي معاملة تمت . مادة (٢٠) توزع الأرباح والخسائر على الشركاء كلا بحسب نسبته من الحصص في الشركة.',NULL,NULL,NULL);
INSERT INTO book_nodes VALUES (116,'دليل الإجراءات','chapter','الفصل السادس : أحكام ختامية',' مادة (٢١): تتحمل الشركة المصروفات والتكاليف التي صرفت في سبيل تأسيس الشركة. مادة (٢٢) عند انتهاء مدة الشركة أو في حالة حلها قبل الأجل المحدد يعين الشركاء مصفيًا وإذا تراضوا لهم الحق فيما تراضوا عليه . مادة (٢٣) : يتم الرجوع إلى أحكام القانون المنظم للشركات التجارية النافذ في كل ما لم ينص عليه في هذا العقد. مادة (٢٤) : حرر هذا العقد من عدد من النسخ لكل متعاقد نسخة ونسختين تودع أحداهما في إدارة الشركات والأخرى في إدارة السجل التجاري. والله ولي التوفيق ،،، م اسم الشريك الجنسية شخصية / جواز التاريخ / مكان الاصدار موبايل + ايميل التوقيع 240 ۱ د ۳ ه - عقد التأسيس الشركة ....................................................................................................................................................................... ( ش . م . ي ) شركة مساهمة مفتوحة اكتتاب عام إنه في يوم ....................................................................................................................................................................................................... الساعة ......................................................................... ه بتاريخ / / الموافق / / م ، بمكان .......................................................................................................................................................................................................................................................................................................................................................................................................................... وبمدينة صنعاء تم الاتفاق بين الموقعين أدناه وهم : وذلك على ما يلي : مادة (۱): أن يؤسسوا شركة يمنية مساهمة مفتوحة ذات اكتتاب عام بناء على ترخيص من رئيس مجلس الوزراء طبقًا لأحكام القانون رقم (٢٢) لسنة ١٩٩٧ م وتعديلاته بشأن الشركات التجارية والنظام الأساسي المرفق. مادة (٢): اسم هذه الشركة : ........................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................ ( ش . م . ي ) اكتتاب عام مادة (۳) : أغراض الشركة : .......................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................... مادة (٤) مركز الشركة : يكون مركز الشركة ومحلها القانوني في مدينة صنعاء ويجوز بقرار من الجمعية العامة غير العادية للشركاء أن تنشئ لها فروعا أو مكاتب أو توكيلات في الجمهورية اليمنية أو في الخارج . مادة (٥) : مدة الشركة : المدة المحددة بالترخيص للشركة هي خمسة وعشرين عامًا ابتداء من تاريخ صدور قرار الترخيص بتأسيس الشركة وكل إطالة لمدة الشركة يجب أن تعتمد بقرار مماثل . مادة (٦): رأس مال الشركة : حدد رأس مال الشركة المصرح به بمبلغ وقدره (.................) ريال يمني موزع على (..............) سهم قيمة كل سهم (۱۰,۰۰۰) ريال ، ورأس المال المدفوع مبلغ وقدرة ...............) ريال يمني . مادة (٧) : أ- اكتتب المؤسسون بنسبة ٧٥% من رأس المال وطرح ٢٥% من رأس المال للاكتتاب العام، وتم توزيعه فيما بينهم على النحو التالي: ب - الأسهم العينية متمثلة في مبنى المنشأة وملحقاتها والمؤسسون مسولين عن الأسهم العينية وبقيمتها الحقيقية ان وجدت. 241 ج - لا يجوز للبنك تسليم المبالغ المودعة لديه لحساب الشركة إلا لمجلس الإدارة بعد إتمام تأسيس الشركة أو تعاد للمؤسسين إذا صرف النظر عن تأسيسها. مادة (۸) وافق المؤسسون على النظام الأساسي للشركة والمرفق بهذا العقد وتعهدوا بالسعي لاستيفاء إجراءات التأسيس والحصول على القرار الوزاري بإنشاء الشركة طبقًا للقانون النافذ. مادة (٩): يتحمل المؤسسون جميع نفقات التأسيس كلا بحسب حصته في رأس مال الشركة في حالة عدم اكتمال إجراءات التأسيس أما في حالة اكتمال تأسيس الشركة فأنها تصبح جزء من النفقات العامة للشركة مادة (١٠) : حرر هذا العقد من عدد من النسخ لكل شريك نسخة ونسختين تودع أحدهما لدى إدارة الشركات في الوزارة ونسخة لإيداعها في السجل التجاري بعد إتمام إجراءات التأسيس . 242 ---0---0---0---0---0---0----0- الملاحق والمراجع يحتوي هذا الفصل على ما يلي :',NULL,NULL,NULL);
