INSERT INTO book_nodes VALUES (162,'دليل الإجراءات','sub_title','أولاد بنت الابن',' بنت ابن العم لأم - العمة أب عم الأب لأم - عمات الأب جد (أبو الأب) أبو الأم (الجد ) - الأخوال والخالات أم أب أم الأب - أخوال وخالات الأب أم الأب أب أم الأم - أخوال وخالات الأم أم الأم بنت الأخ الشقيق الأخ الشقيق بنت الأخ لأب الأخ لأب',47,NULL,NULL);
