INSERT INTO book_nodes VALUES (165,'دليل الإجراءات','sub_title','أولاد الأخت لأب',' الأخت لأب بنت ابن الأخ الشقيق ابن أخ شقيق بنت ابن أخ لأب ابن أخ لأب بنت عم شقيق عم شقيق م ۱ د ۳ ٤ 。 ٦ V A ۹ ۱۰ ۱۱ ال ۱۳ ١٤ ١٥ 374 م الوارث من ذوي الرحام الوارث الذي يرث مثل نصيبه ١٦ بنت عم لأب عم لأب ۱۷ بنت ابن عم شقيق ابن عم شقيق ۱۸ بنت ابن عم لأب ابن عم لأب - الحجب (الإسقاط): • الحجب نوعان :- ١- حجب نقصان ٢ - حجب حرمان (الإسقاط ) وإذا أطلق لفظ الحجب فالمراد به حجب الحرمان (الإسقاط ) . • خمسة لا يحجبون أبدًا ( لا يسقطون ) وهم : 1 - أحد الزوجين (الزوج والزوجة ) ٢ الابن ٣ - البنت ٤- الأب ٥- الأم • جدول الحجب : م المحجوب الحاجب ابن الابن بنت الابن الجد ( أبو الأب) الجدة (أم الأم وأم الأب) الأخ الشقيق والأخت الشقيقة الأخ لأب الأخ لأم / الأخت لأم بالابن بالابن وبالبنتين فصاعدا ما لم تعصب بالأخ المبارك بالأب بالأم، وبالأب اذا كانت الجدة أمه، وبالجدة الأقرب منها بالابن وابن الابن وبالأب بالأخ الشقيق وبما يحجب به وبالأخت الشقيقة اذا كانت عصبة مع البنت او بنت الابن لأنها في قوة الأخ الشقيق بالفرع الوارث (ابن ابن ابن ، بنت ، بنت ابن ) ، وبالأصل الوارث (أب، جد ) 375 ۱ د ۳ ٤ 。 ٦ V م المحجوب الحاجب ٨ الأخت لأب بما يحجب الأخ لأب وبالعدد من الأخوات الشقيقات مالم تعصب بالأخ المبارك ۹ ابن الأخ الشقيق بالأخ لأب وبما يحجب به وبالأخت لأب إذا كانت عصبة مع البنت أو بنت الابن لأنها في قوة الأخ لأب ۱۰ ۱۱ ابن الأخ لأب العم الشقيق بابن الأخ الشقيق وبما يحجب به بابن الأخ لأب وبما يحجب به ۱۲ ۱۳ ١٤ ١٥ العم لأب ابن العم الشقيق بالعم الشقيق وبما يحجب به بالعم لأب وبما يحجب به ابن العم لأب بابن العم الشقيق وبما يحجب به المعتق بابن العم لأب وبما يحجب به',47,NULL,NULL);
INSERT INTO book_nodes VALUES (166,'دليل الإجراءات','sub_title','ثالثا : قواعد حساب مسائل المواريث','',47,NULL,NULL);
INSERT INTO book_nodes VALUES (167,'دليل الإجراءات','sub_title','أولا : مسائل المواريث البسيطة',' أ - أصول المسائل : يتم تقسيم التركة حسابيًا بتقدير التركة بعدد رمزي، وهي مجموعة من الأسهم يتم معرفة قيمة السهم بقسمة التركة على عدد الأسهم، ويُعرف مجموع الأسهم لأول مرة بأصل المسألة. فأصل المسألة : هو أقل عدد يمكن أن تؤخذ منه سهام الورثة بدون كسر. ويختلف تقدير الأسهم حسب أصناف الورثة ما بين عصبات فقط أو أصحاب فروض فقط أوكلاهما كما يلي : 376 أ) حساب أصل المسألة في حالة العصبات فقط وهنا يكون الورثة عصبات فقط ، سواء كانوا ذكورًا أم ذكورا وإناثا كما يلي : ١ - فإذا كانوا ذكورا : كان أصل المسألة بعدد رؤوس الورثة . قاعدة (۱) : أصل المسألة في حالة العصبات الذكور = عدد رؤوس الورثة مثال : سبعة أبناء، فأصل المسألة من سبعة ، أو خمسة أبناء ابن ، فأصل المسألة من خمسة ، وهكذا جميع العصبة بالنفس الإخوة الأشقاء أو لأب ، وأبناء الإخوة الأشقاء أو لأب، والأعمام الأشقاء أو لأب ، وأبناء الأعمام الأشقاء أو لأب. ٢ - أما إذا كانوا ذكورا وإناثا : فإن أصل المسألة يكون من مجموع رؤوس الإناث مع احتساب كل ذكر بأنثيين، ويكون ذلك في حالة العصبة بالغير مع معصبهم كالبنات مع الأبناء، وبنات الابن مع أبناء الابن والأخوات الشقيقات مع الإخوة الأشقاء، والأخوات لأب مع الإخوة لأب. قاعدة (٢) : أصل المسألة في حالة العصبة الذكور والإناث = ( عدد الذكور 2) + عدد الإناث مثال: خمسة أبناء وست بنات، فتقدر الورثة كلهم إناث والذكر بأنثيين هكذا (٢x٥) + 6 = ١٦ سهما وعند القسمة يُعطى الذكر سهمين. مثال آخر: سبعة إخوة لأب وتسع أخوات لأب فأصل المسألة = (2x7) + ٩ = 23 سهم. ب ) حساب أصل المسألة في حالة صاحب فرض واحد مع صاحب عصبة ويكون أصل المسألة من مخرج صاحب الفرض مثال: ماتت عن زوج وابن فأصل المسألة من مخرج الربع (٤) وهو فرض الزوج . مثال آخر: مات عن زوجة وابن فأصل المسألة من مخرج الثمن (۸) وهو فرض الزوجة قاعدة (۳) : أصل المسألة في حالة صاحب الفرض الواحد مع العصبة = (مخرج الفرض. ج ) حساب أصل المسألة في حالة أصحاب الفروض فقط وهنا يكون الورثة من أصحاب الفروض المقدرة في القرآن (نصف - ربع - ثمن - ثلثان - ثلث - سدس ) . 377 1 - حساب أصل المسالة في حالة أصحاب الفروض فقط: ويكون أصل المسألة هنا من مخرج مقامات الفروض وهو العامل المشترك للأعداد في مقامات الفروض المقدرة ( ٢ - ٣ - ٤ - ٦ - ٨ ) ، وأكبر مقام مشترك هو ٢٤ ويتم استخراج العامل المشترك بالطريقة الحديثة عن طريق التحليل وبالطريقة التقليدية حسب العلاقة بين الأعداد كما يلي : ١- ففي حالة التماثل بين الأعداد : وهو تساوي الأعداد فيكون أصل المسألة أحد العددين. قاعدة ( ٤ ) : أصل المسألة في حالة التماثل = أحد العددين مثال: النصف والنصف [ زوج (+) - أخت ( ) ] ، أصل المسألة من العدد (٢). ٢ - أما في حالة التداخل بين الأعداد : وهو عددان ينقسم فيه الكبير على الصغير من غير باقي، فيكون أصل المسألة العدد الكبير. قاعدة ( ٥ ) : أصل المسألة في حالة التداخل = العدد الكبير مثال : النصف والربع [ أخت () - زوجة (1)] فأصل المسألة من ٤. مثال آخر: وكذا النصف والثمن [ بنت ( ) - زوجة (7) ] فأصل المسألة من . مثال آخر: النصف والسدس [ بنت (+) - أم (+)]، فأصل المسألة من ٦ . مثال آخر: الثلث والسدس [ أخوين لأم ( ) - أم (4)]، فاصل المسألة من ٦ . - أما حالة التوافق بين الأعداد : ويكون بوجود عدد مشترك يقسم العددين من غير باقي . قاعدة (٦) : أصل المسألة في حالة التوافق = أحد العددين × وفق العدد الثاني | ناتج قسمة العدد الآخر على العدد المشترك ) مثال : الربع والسدس [ زوجة (1) - جدة ( ) ] ، فالعامل المشترك هو ٢ ، فيتم أخذ وفق ال (٤) وهو (٢) ويضرب في كامل الآخر ( ٦٢ ) أو كامل ال (٤) × وفق ال (٦) وهو (٣) فتصير (٣٤) والناتج هو (١٢) وهو أصل المسألة . مثال آخر: الثمن والسدس [ زوجة (1) - بنت ( ) - أم (+) ] والنظر هو للثمن والسدس لأن النصف يدخل فيهما ، فالعامل المشترك هو (٢) فيتم أخذ وفق أحدهما 378 كامل الآخر (٦٠٤ ) أو ( ٣٨ ) والناتج هو (٢٤) وهو أصل المسألة. ٤- أما في حالة تباين الأعداد : وهو عدم وجود عدد مشترك يقسمهما فيكون أصل المسألة بضرب العددين. قاعدة (۷) : أصل المسألة في حالة التباين = حاصل ضرب العددين المتباينين مثال : النصف والثلث [ زوج ( ) - أم ( ) ] ، فحاصل ضرب العددين (٣٢) = (٦) هو أصل المسألة. ٢ - حالات أصحاب الفروض فقط: وتتعدد حالات حساب أصل المسالة في حالة أصحاب الفروض فقط إلى ثلاث حالات : ١- حالة تساوي التركة (الأسهم ) مع نصيب الورثة : ( ويُعرف بالاستكمال أو العادلة): مثال : [ زوج () - أخت الشقيقة (+)]، فأصل المسالة من ؟ للزوج سهم وللأخت السهم الآخر. مثال آخر: [ أخت ش () - أخت لأب (+) - أخوين لأم (۳) ]، فأصل المسألة من ٦ للأخت الشقيقة ثلاثة أسهم، وللأخت لأب سهم وللأخوين لام سهمان لكل أخ سهم . ٢ - حالة زيادة التركة (الأسهم ) عن نصيب الورثة : ( ويُعرف بالرد : حيث يبقى هنا مال بعد أخذ كل وارث نصيبه ، فهنا يُرد الباقي على الورثة ، ويكون حساب أصل المسألة كما يلي : أ- الورثة صنف واحد : [ خمس أخوات ش (۴) ] ، فيكون أصل المسألة من عدد رؤوسهم (٥) كحالة العصبة المنفردين الذكور. ب - الورثة من أصناف مختلفة : بشرط عدم وجود أحد الزوجين [ أخت ش (+) - أم (۳)]، فأصل المسالة من ٦ للأخت ش ۳ ، وللأم ٢ فيرد أصل المسألة إلى 5 مجموع السهام فيكون نصيب الأخت ٣ أسهم من ٥ ، والأم سهمين من ٥ . ج - في حالة وجود أحد الزوجين : فهنا لا يكون الرد على الزوجين ويكون لباقي الورثة فقط، ويتم عمل مسألتين : الأولى لأحد الزوجين، والثانية لباقي الورثة المردود عليهم كما سيأتي في المسائل المركبة. 379 كسور إلى جبر أي ليس فيه كسر، وتعرف هذه العملية بالتصحيح ، ولا يكون التصحيح في حالة العصبات ؛ لكون الكسر هنا مستحيل . ويتم التصحيح بضرب أصل المسألة في رقم (يُعرف بجزء السهم) ، حسب القاعدة التالية : قاعدة (۸) : المسألة بعد التصحيح = أصل المسألة ( ولو بعد الرد أو العول ) × جزء السهم. ب ) طريقة حساب جزء السهم: يُعرف جزء السهم بأنه مخرج الوارثين الذين في نصيبهم كسر، ويتم استخراج جزء السهم بمرحلتين : المرحلة الأولى : ملاحظة العلاقة العددية بين المال ( عدد سهام الورثة )، والرؤوس ( عدد الورثة الذين في نصيبهم كسر) ، وهي إما : تداخل أو توافق أو تباين ، كالآتي : ١- حالة التداخل : ويكون فيها عدد رؤوس الورثة ينقسم على سهامهم دون باقي ، كأن يكون عدد رؤوس الورثة ، وعدد السهام ٤، فيكون جزء السهم حسب القاعدة التالية: قاعدة (۹) : جزء السهم في حالة التداخل = عدد رؤوس الورثة : السهام. مثال : [ ٨ أخوات ش (4) - أم (4) - أخ لأم (4) ]. فأصل المسألة هنا من ٦ أسهم نصيب الأخوات الشقيقات ٤ أسهم وعدد رؤوس 8 اخوات، والعلاقة هنا تداخل بين ٨ و ٤ ، فيكون جزء السهم ٨ ٢٤ ، يتم ضربه في أصل المسألة فيكون أصل المسألة بعد التصحيح = 6 × 2 = ١٢ سهما ٢- أما في حالة التوافق : فيكون العلاقة بين عدد سهام الورثة ورؤوسهم هو وجود عدد مشترك يقسم العددين دون باقي ، فنختار أكبر عدد مشترك، كأن يكون عدد رؤوس الورثة ١٦ ، وعدد السهام ٢٤ ، فيكون العدد المشترك الذي يقسمهما إما ٢ أو ٤ أو ٨، فأكبر عدد هو ۸ ، وتكون القاعدة كالتالي : قاعدة (۱۰) : جزء السهم في حالة التوافق = عدد الرؤوس : أكبر عدد مشترك مثال 1 : [ زوجة ( ) - أم (+) - ٦ بنات ( ) - عم (ع) ] فأصل المسألة هنا من ٢٤ سهما ، نصيب البنات (١٦) سهمًا وعدد رؤوسهن ٦، والعلاقة هنا توافق بين ١٦ و ٦ ، والعدد المشترك بينهما ٢ ، فيكون جزء السهم ٦ - ٢ - ٣ ، يتم ضربه في أصل المسألة فيكون أصل المسألة بعد التصحيح = 3 × ٢٤ = ٧٢ سهما . 381 مثال : زوجة (1) - أم (+) - ٢٤ بنتا ( ب ) ، عم (ع) فأصل المسألة (٢٤) سهما، نصيب البنات (١٦) سهمًا وعدد رؤوسهم (٢٤) والعدد المشترك (٢ ٤٠، ٨) فجزء السهم = ٢٤ - ٨ = ٣ فيكون أصل المسألة بعد التصحيح ٣ ٢٤ = ٧٢ . - أما في حالة التباين : وتكون فيه العلاقة بين المال والرؤوس متباينة بمعنى أنهما ليس بينهما تداخل ولا يوجد عدد مشترك يقسمهما ، فيكون جزء السهم هنا حسب القاعدة التالية : قاعدة (۱۱) : جزء السهم في حالة التباين = عدد رؤوس الورثة الذين في نصيبهم كسر مثال : [ ٤ زوجات (1) - أم (+) - أب (4) - بنت ()] فأصل المسألة هنا من ٢٤ أسهم نصيب الزوجات ٣ أسهم ، وعدد رؤوسهن ٤، والعلاقة هنا تباين بين ٣ و ٤ ، فيكون جزء السهم عدد الرؤوس = ٤ ، يتم ضربه في أصل المسألة فيكون أصل المسألة بعد التصحيح = ٢٤٠٤ = ٩٦ سهما . المرحلة الثانية : وتكون في حالة تعدد أصناف الورثة الذين في نصيبهم كسر، وفي هذه الحالة نسمي كل صنف من هؤلاء الورثة بمحفوظ أول وثاني وهكذا حسب عددهم، ونستخرج جزء السهم حسب القاعدة التالية : قاعدة (۱۲) : جزء السهم في حالة تعدد الرؤوس الذين في نصيبهم كسر = مخرج المحفوظات | (العامل المشترك) مثال : [ زوجتان (1) - جدتان (+) - ١٠ إخوة لأم () - ٩ إخوة لأب (ع) ] أصل المسألة من ١٢ سهما، ورؤوس الورثة الذي حصل في نصيهم كسر على ثلاثة أصناف، فنعمل لكل صنف محفوظ ثم نعمل العامل المشترك بينهم فيكون هو جزء السهم، كما يلي : محفوظ أول : متعلق بصنف الإخوة لأب عدد الرؤوس ۹ وسهامهم ، العلاقة تداخل، وجزء السهم = عدد الرؤوس : السهام = ۹ : 3 = ٣ . محفوظ ثان متعلق بصنف الإخوة لأم عدد الرؤوس ۱۰ وسهامهم ٤ العلاقة توافق، بالرقم المشترك ٢ جزء السهم = عدد الرؤوس : العدد المشترك = ١٠ : ٢ = ٥ . محفوظ ثالث: متعلق بصنف الزوجتين عدد الرؤوس ٢ وسهامهم الربع 3 أسهم، 382 والعلاقة تباين ، فيكون المحفوظ عدد الرؤوس (٢). المخرج المشترك للمحفوظات : ٣ ، ٥ ، ٢ = ٣٠ × أصل المسألة ١٢ = ٣٦٠ سهما . ج ) حل المسائل بطريقة القراريط وفي هذه الحالة يتم تقسيم أي تركة من أربعة وعشرين جزءا تُسمى القراريط؛ لأن هذا الرقم هو أكبر عامل مشترك يجمع أصحاب الفروض ( - - - - ) ، ويتم قسمة التركة على ٢٤ ولو كان نصيب الوارث فيه كسر حيث لا يحتاج إلى تصحيح . وفي حالة العول أو الرد فيتم معرفة نصيب كل وارث بالقراريط أولا بمعرفة نصيب الوارث بالأسهم ثم تحويلها إلى قراريط حسب القاعدة التالية : قاعدة (۱۳) : قيمة سهم الوارث بالقراريط = نصيب الوارث بالأسهم × ٢٤ : أصل المسألة قاعدة (١٤) : قيمة سهم الوارث بالأسهم = نصيب الوارث بالقراريط » أصل المسألة : ٢٤ .',47,NULL,NULL);
INSERT INTO book_nodes VALUES (168,'دليل الإجراءات','sub_title','ثانيا: مسائل المواريث المركبة:',' وهي عبارة عن وجود أكثر من مسألة يتم جمعها في مسألة واحدة عن طريق وجود عامل مشترك بينهما ، وسبب وجود أكثر من مسألة يرجع للأسباب التالية : ١- بسبب استقلال بعض الورثة ( الزوجين ) بمسألة خاصة بهم، ومسألة أخرى خاصة بباقي الورثة وهي في حالتين. أ- حالة الرد مع الزوجين : ( زوجة - جدة - أخ لام ) ؛ لأنه لا رد للزوجين. ب - حالة ذوي الأرحام مع الزوجين : (زوجة - خال - بنت عمة)؛ لأنه لما كان لا رد للزوجين، فيكون الباقي لذوي الأرحام، مع مراعاة أن ذوي الأرحام لا يؤثرون على نصيب أحد الزوجين بالحجب أو بالنقصان، ثم يتم عمل مسألة جامعة للمسألتين، بالطرق الحسابية للجمع. ٢ - بسبب موت بعض الورثة قبل قسمة مال مورثه (المناسخات ) : حيث نقدر هنا مسألة لكل وارث متوفى، وننظر للعلاقة بين تركته ومسألته، ثم نضربها في أصل المسألة، وإذا تعدد الورثة المتوفون نستخرج العامل المشترك لمسألتهم، ثم نضربها في أصل المسألة ، مثل طريق التصحيح. 383 - بسب الشك في معرفة الوارث والمورث ( إرث الغرقى والهدمي ) : وهم الذين يموتون في حادث واحد، فنقدر لكل متوفي مسألة نقدر فيها حياة المتوفي الآخر ونورثه منها ، ثم نعطي نصيبه لورثته عن طريق المناسخة، كما نقدر مسألة أخرى للمتوفى الآخر نقدر فيها حياة المورث الأول، ونعطيه من التركة من أصل المال دون ما صار إليه من المتوفى الآخر، ثم نعطيه لورثته عن طريق المناسخة. - بسب الشك في حياة الوارث (إرث المفقود) : حيث نعمل مسألتين الأولى نقدر فيها حياة الوارث (المفقود ) ، والثانية نقدر فيها موته ثم نجمع بينهما بمسألة جامعة، ونوقف نصيب المفقود حتى يظهر حياته أو موته ، فإذا ثبتت حياته أعطى نصيبه ، وإن ظهر وفاته اقتسم نصيبه باقي الورثة كل بحسب نصيبه. ه - بسبب الشك في نوع الوارث ذكرا أم أنثى (إرث الخنثى ) : وهنا نعمل أيضا مسألتين، الأولى نقدر الحمل ذكراً والأخرى نقدره أنثى ، ونجمع بينهم بمسألة ونعطيه أقل نصيب ، ونوقف الباقي حتى ينكشف نوعه ، فإن بان الأكثر أعطي له باقي نصيبه ، وإن ظهر الأقل يُقسم الموقوف بين الباقي الورثة كل بحسب نصيب . ٦ - بسبب الشك في حياة ونوع الوارث (إرث الحمل ) : وهنا نعمل أكثر من مسألة نقدر في الأولى وفاة الحمل، وفي الثانية حياة ذكرًا، وفي الثالثة حياته أنثى مفردة ، وفي الرابعة حياته أنثى متعددة - إذا لم يكن معها معصب - ، ونعمل لتلك المسائل مسألة جامعة ، ثم نقسم المسألة على أقل نصيب للحمل، وهو تقدير وفاته ، ونوقف الباقي حتى يظهر حال الحمل ، كما هو في المفقود والخنثى. - بسبب تخيير الوارث بين عدة حالات (الجد مع الإخوة ) : وهنا يُفترض أن نقدر للوارث (الجد ) عدة حالات نعطيه أفضلها بعد أن نعمل مسألة جامعة، وهي مسائل كاشفة عن معرفة الأفضلية للجد ، وتختلف تقدير الحالات كما يلي : أ- إذا اجتمع الجد مع الإخوة أو مع الأخوات إذا تعصبن بأي نوع من العصبة : فنقدر هنا مسألتين الأولى : يكون للجد فيها المقاسمة ، والثانية : يكون له فيها السدس ونعطيه أفضلهما. ب - إذا كان الجد مع أخوات لم يتعصبن بأي نوع من العصبة : وهنا نقدر ثلاث مسائل، 384 الأولى : يكون للجد فيها بقية المال عصبة بعد أصحاب الفروض ، والثانية : نعطيه المقاسمة، والثالثة : السدس ، ونعطيه أفضل الحالات، بعد عمل مسألة جامعة .',47,NULL,NULL);
INSERT INTO book_nodes VALUES (169,'دليل الإجراءات','sub_title','ثالثا : المسائل الملقبة ذات الألقاب):',' وهي عبارة عن مسائل مشهورة خالفت فيها القواعد العامة واشتهرت باسم معين كما يلي : ١- مسألة العمريتين (الغراوين): وهي مسألة أحد الزوجين مع الوالدين فقط. فالأصل فيها أن نعطى الأم ثلث المال كاملا ، ولكن لما كان ذلك قد يزيد عن نصيب الأب، أو يجعل النسبة بين الأب والأم أكثر من نسبة ١:٢ للذكر مثل حظ الأنثيين، فهنا نعطي الأم ثلث الباقي، كما يلي : أ - [ زوج ( ) - أب (ع ) - أم ( ( ] : للأم ثلث الباقي وهو السدس في الحقيقة. ب - [ زوجة ( ) - أب (ع ) - أم ( ) ] : للأم ثلث الباقي وهو الربع في الحقيقة. ٢ - المسألة المشتركة : وهنا يُفترض أن المسألة حصل فيها استكمال لأصحاب الفروض، وبالتالي يسقط حق العصبة لعدم وجود مال، ولكن لما كان من أصحاب الفروض إخوة لأم وارثين، وفي العصبة أخ شقيق لأب وأم لا يرث فكانت العدالة أن يرث كونه أقرب نسبًا للميت من جهة الأب والأم بينهما الأخ لأم من جهة لأم ؛ لذلك تم توريث الأخ الشقيق مع الإخوة لأم واشتركوا في الثلث فسميت المشتركة. مثال : [ زوج ( ) - جدة ( ) - إخوة لأم ( ) - أخ شقيق (ع) ]، فيتم اشتراك الأخ أو الإخوة الأشقاء مع الإخوة لأم في الثلث لأن أمهم واحدة.',47,NULL,NULL);
INSERT INTO book_nodes VALUES (170,'دليل الإجراءات','sub_title','رابعا مسائل متفرقة',' ۱ - حساب الإقعاد وهو طريق لاستحقاق أولاد الابن من تركة بطريق رضائي، حيث يتم برضا الجد بالإقعاد لأولاد ابنه ليقوموا مقام والدهم استحقاقا للتركة لا إرثا، ولا يوجد على هذه الحالة إلا شرط عدم مجاوزة الثلث ما لم يجز الورثة ذلك . أصل المسألة بعد الإقعاد = أصل المسألة قبل الإقعاد + نصيب الوارث المقعد 385 مثال : زوجة / ثمن - ابن / عصبة بالنفس - ٣ بنات / عصبة بالغير - ابن مقعد أصل المسألة قبل الإقعاد / ٤٠ + نصيب الابن المقعد / ١٤ - ٥٤ سهما (أصل المسألة بعد الإقعاد ) مثال : زوجة / ثمن - ابن / عصبة بالنفس - بنت / عصبة بالغير - ابن مقعد أصل المسألة قبل الإقعاد / ٢٤ + نصيب الابن المقعد / ۸ ( حيث تم انقاص نصيب الابن من ١٤ إلى الثلث / ٨ ؛ لأنه تجاوز الثلث ( = ٣٢ سهما ( أصل المسألة بعد الإقعاد). ٢ - حساب الوصية الواجبة : وهو طريق لاستحقاق أولاد الابن من تركة بطريق إجباري عن طريق القضاء، ويكون في حالة عدم صدور الإقعاد من الجد، ويرد على نظام الوصية الواجبة عدة قيود وردت في (م ٢٥9) من قانون الأحوال الشخصية، بشرط أن يكون أولاد الابن فقراء، والمورث لديه تركة كثيرة، ومن ضوابط حساب الوصية الواجبة ما يلي : نصيب البنات فقط / السدس - نصيب أولاد الابن الواحد ذكورا فقط أو مع الإناث / الخمس - في حال تعدد الأبناء المتوفين فيكون لهم الثلث. ملاحظة : في حساب الإقعاد والوصية الواجبة يكون النقص في الأسهم على جميع الورثة باعتبار الوصية يتم إخراجها من رأس التركة. ٣ - حساب التخارج والتخارج : هو تصالح الورثة مع أحد الورثة أو بعضهم بأن يتنازلوا عن نصيبهم من التركة مقابل الحصول على مال معين . أصل المسألة بعد التخارج = أصل المسألة قبل التخارج - نصيب الوارث المتخارج مثال : زوجة / ثمن تخارجت - أم / سدس - ابن / عصبة بالنفس - بنت / عصبة بالغير أصل المسألة قبل التخارج / ٧٢ - نصيب الوارث المتخارج / الزوجة / ٩ = ٦٣ سهما (أصل المسألة بعد التخارج). 386',47,NULL,NULL);
INSERT INTO book_nodes VALUES (171,'دليل الإجراءات','sub_title','سادسا : قواعد الحساب والمساحة',' ١ - الرموز الحسابية م الرمز ( ٦٤ ) الصورة بالكسر العادي بالكسر العشري نسبة % ۱ إلا ربع بع ¾ ٠,٧٥ %٧٥ ٢ نصف - ½ ٠,٥ %٥٠ ٣ ربع < ¼ ٠,٢٥ %٢٥ ٤ ثمن ء ⅛ ٠,١٢٥ %١٢,٥ ٥ نص الثمن X 0 %٦ ٦ ربع الثمن ≥ ٠ %٣ ٧ ثمن الثمن -- ٠ %١ ٨ نصف ثمن الثمن - ٠ %٠ ٢ - وحدة مساحة قياس الأراضي في محافظات الجمهورية اليمنية: النوع اسم اللبنة الجهة بالمتر المربع بالذراع اللبنة الصنعاني عشاري ٤٤,٤٤ متر مربع ۱۰۰ ذراع اللبنة العمراني اثنا عشري ٦٤ متر مربع ١٤٤ ذراع اللبنة اللبنة الذماري ١١٤,٤٩ متر مربع اللبنة المحويتي ٦٤ متر مربع 387 النوع اسم اللبنة الجهة بالمتر المربع بالذراع القصبة الإبي ٥٦,٢٥ متر مربع القصبة القصبة التعزي عشاري ٢٠,٢٥ متر مربع ٤٧ ذراع الحبلة الصعدي ۲۸ متر مربع الحبلة الحبلة الرداعي ۸۱ متر مربع الحبلة الجوفي ۹ متر مربع المعاد التهامي ٤٣٥٥,١٢ متر مربع المعاد المعاد الزبيدي ٣٦٠٠ متر مربع الفدان ٢٠٠ ، ٤ متر مربع القيراط ١٧٥ متر مربع ٣- طرق المسح والحساب الهندسي الاسم الشكل المساحة المحيط مربع ل المساحة = طول المحيط = طول الضلع × نفسه الضلع ٠ ٤ المساحة = ل × ل المحيط = 4 × ل 388 الاسم الشكل المساحة المحيط مستطيل ل ع المساحة = الطول × العرض المساحة = ل × ع المحيط = ٢ (الطول + العرض ) المحيط = ٢ ( ل + ع) مثلث أ د ه ج المساحة = طول القاعدة * الارتفاع المساحة = ده المحيط = مجموع أضلاع المثلث المحيط = أ + ب + ج المحيط = أج + ج ب + ب أ متوازي أضلاع ب ع ل ج د المساحة = طول القاعدة × الارتفاع المساحة = ل × ع المحيط = ٢ × (مجموع الضلعين المتجاورين ) المحيط = 2 × (أج + ج د ) معين ع ل ج المساحة = ( طول القاعدة * الارتفاع ) : ٢ المساحة = ل × ع ) المحيط = 4 × طول الضلع المحيط = ٤ج 389 المساحة المحيط الاسم الشكل المحيط = مجموع أ أطوال أضلاعه المساحة = ب ( أ + ب ) × ه (أ ب) د المحيط = أ + ب + ج + د ج شبة منحرف المساحة = \( \pi \) نق؟ المساحة = \( \pi \) نق؟ المساحة = ( \( \pi \) ن ) | المساحة = ( \( \pi \) ن (2) حيث أن : ن : نصف حيث أن : ن : نصف قطر الدائرة قطر الدائرة ن دائرة ٢٢ π ==١٤٣ ٢٢ π ==١٤٣ 390',47,NULL,NULL);
INSERT INTO book_nodes VALUES (172,'دليل الإجراءات','sub_title','سابعا : قائمة المراجع',' 1- أسس نظام التوثيق في الجمهورية اليمنية - للقاضي أحمد حزام أحمد القبلاني - منشور بالمجلة القضائية - العدد الثاني - شتاء - عام : ٢٠١٢ م - محرم ١٤٣٤ه - الموافق : ٢٠١٢/١١م. ٢ - أهمية التوثيق العدلي والآثار المترتبة على المنازعات القضائية - للقاضي عبد المجيد محمد عبد القادر - مستشار مصلحة الأحوال المدنية والسجل المدني - منشور بالمجلة القضائية - العدد الثاني - شتاء - عام : ٢٠١٢ م - محرم ١٤٣٤ه - الموافق نوفمبر ٢٠١٢م. - التوثيق بالكتابة والعقود - د. حسين الترتوري - دار ابن حزم - القاهرة - ط ١ - ١٤٢٦ه - . ٢٠٠٥م - الجوانب الفنية والقانونية لصياغة العقود - عبد الرحمن عز الدين الشعيبي - عدنان عبد الكريم الغفاري - المركز الاستراتيجي للقانون - ط ١ - ٢٠١٢م . ه - خلاصة الكلام في أحكام المواريث في الإسلام - د . درويش أحمد مضوني الأهدل - مكتبة الأمين - صنعاء - ٢٠١٣ م . ٦- الصكوك والتوثيقات في المحاكم الشرعية - دار الثقافة - عمان - الأردن - ط ١ - ٢٠١٠ م - ١٤٣١م. - الفرائض في الشريعة الإسلامية دراسة مقارنة بالنظم القانونية - القاضي حسين محمد المهدي - مكتبة خالد ابن الوليد - ط ١ - ١٤٣٧ ه - ٢٠١٦ م . - الفرصة المتاحة لتعليم المساحة - علي بن محمد بن عثمان الوزير - مكتبة خالد ابن الوليد - . ط 2 - 2011م - فن الصياغة القانونية - د. مصطفى محمد المرشدي - دار الجامعة الجديدة - ٢٠١٤ م . ١٠ - قانون الإثبات الصادر بالقرار رقم: (٢١) - لسنة ١٩٩١م - وتعديلاته. ١١ - قانون الإجراءات الجزائية الصادر بالقرار رقم: (١٣) - لسنة : 1994م. ١٢ - قانون الأحوال الشخصية رقم (٢٠) لسنة ١٩٩٢م وتعديلاته. ۱۳ - قانون الأحوال المدنية والسجل المدني - الصادر بالقرار رقم : (٢٣) - لسنة : ١٩٩١م. ١٤ - القانون التجاري بالقرار رقم : (٣٢) - لسنة : ١٩٩١م - والمعدل بالقانون الصادر بالقرار رقم: (١٦) - لسنة : ١٩٩٨م. 15- قانون التوثيق الجديد - الصادر بالقرار رقم: (7) - لسنة : 2010م. 391 ١٦- قانون الخدمة المدنية - الصادر رقم: (۱۹) - لسنة : ١٩٩١م. ۱۷ - قانون السجل التجاري. ۱۸ - قانون السجل العقاري الصادر بالقرار رقم : (٣٩) - لسنة : ١٩٩١م. ۱۹ - قانون الشركات التجارية. ٢٠ - القانون الصادر بالقرار رقم: (١٢) - لسنة : ١٩٩٤م - بشأن الجرائم والعقوبات الي