"""Compatibility stubs for CSP decorators.

Some Django installations may expect `django.views.decorators.csp` to
provide `csp_override` and `csp_report_only_override`. Provide minimal
no-op implementations so imports succeed. This is a temporary workaround
â€” prefer installing a proper Django build or the package that provides
real CSP handling.
"""

from typing import Callable, Any, Dict


def csp_override(policy: Dict[str, Any]) -> Callable:
	def decorator(view_func: Callable) -> Callable:
		return view_func

	return decorator


def csp_report_only_override(policy: Dict[str, Any]) -> Callable:
	def decorator(view_func: Callable) -> Callable:
		return view_func

	return decorator


__all__ = ["csp_override", "csp_report_only_override"]
